INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 1, 2, 'Attività consultiva', 'Si classificano in questa posizione i documenti relativi all’attività consultiva ed esplicativa in materia di normativa provinciale. Si classificano in questa posizione anche le circolari e le direttive.');
