INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 2, 2, 'Normativa statale e delle Regioni: analisi e verifica', 'Si classificano in questa posizione i documenti relativi all’attività di analisi, verifica e valutazione degli atti normativi dello Stato e delle Regioni.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 2, 3, 'Normativa statale e delle Regioni: attività consultiva', 'Si classificano in questa posizione i documenti relativi all’attività consultiva ed esplicativa relativa alla normativa dello Stato e delle Regioni. Si classificano in questa posizione anche le circolari e le direttive.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 3, 0, 'Normativa dell’Unione Europea 1.3.1 Attività di analisi', 'Si classificano in questa posizione i documenti relativi all’attività di analisi, monitoraggio e verifica della normativa dell’Unione Europea, nonché delle procedure precontenziose e contenziose che chiamino in causa disposizioni dell’ordinamento provinciale. Si classificano in questa posizione anche i documenti relativi alla formazione degli atti comunitari nonché quelli relativi agli atti comunitari non normativi, quali pareri e raccomandazioni.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 3, 2, 'Attività consultiva', 'Si classificano in questa posizione i documenti relativi all’attività consultiva ed esplicativa in materia di normativa dell’Unione europea. Si classificano in questa posizione anche le circolari e le direttive.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 4, 0, 'Denominazione, territorio e confini', 'Si classificano in questa posizione i documenti relativi alla definizione territoriale della Provincia, comprese eventuali modifiche di confini, denominazione ecc. Nello specifico, si classificano in questa posizione i documenti relativi alle procedure di aggregazione al territorio provinciale di comuni siti in altre regioni così come quelli relativi alle procedure di distacco di comuni trentini.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 5, 0, 'Cerimoniale, bandiera, gonfalone, stemma logo', 'Si classificano in questa posizione i documenti relativi al cerimoniale, all’uso delle bandiere della Repubblica italiana e dell’Unione Europea, al conferimento e alla ricezione di onorificenze e premi di rappresentanza. Si classificano inoltre in questa posizione i documenti relativi alla bandiera, allo stemma, al gonfalone, al logo della Provincia, nonché quelli relativi alla concessione del loro utilizzo per attività organizzate da terzi. Si classificano in questa posizione anche i documenti ricevuti e/o spediti per attività di relazioni pubbliche non altrimenti riconducibili a specifiche partizioni del titolario (saluti, condoglianze, inviti ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 6, 0, 'Elezioni', 'Si classificano in questa posizione i documenti relativi alle attività di competenza provinciale in materia di consultazioni referendarie ed elettorali per l’elezione del Consiglio provinciale e del Presidente della Provincia. In particolare, si classificano in questa posizione i documenti relativi alla gestione degli albi dei presidenti di seggio (comunicazioni dei sindaci relative a iscrizioni/cancellazioni dall’albo e pubblicazione dell’albo), alle procedure connesse alla convocazione dei comizi elettorali e alla presentazione delle liste, agli aspetti organizzativi e alla predisposizione di materiali elettorali (stampa schede, bolli, urne, ricevute dei comuni per la consegna di materiale elettorale, verbali di carico/scarico cartoline avviso per elettori residenti all’estero), agli interventi, in caso d’inadempienza dei sindaci, in merito all’esistenza e al buono stato dell’arredamento dei seggi. Si classificano in questa posizione anche i documenti trasmessi a seguito della conclusione delle operazioni elettorali (schede, tabelle di scrutinio, verbali di nomina degli scrutatori e del segretario, verbali delle operazioni elettorali sezionali, comunicazioni di affluenza al voto), nonché documenti relativi all’esercizio del voto elettronico.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 7, 0, 'Ordinamento e organizzazione delle strutture', 'Si classificano in questa posizione i documenti relativi agli atti organizzativi delle strutture e del personale provinciale (organigramma, funzionigramma, dotazioni organiche, orari di apertura degli uffici, attribuzioni di uffici ecc.). Si classificano in questa posizione anche i documenti relativi alle declaratorie e alle proposte di riassetto delle strutture.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 8, 0, 'Semplificazione amministrativa', 'Si classificano in questa posizione i documenti relativi alle attività di semplificazione dell’azione amministrativa, ivi comprese quelle connotate da interventi di innovazione organizzativa e tecnologica. Si classificano in questa posizione, altresì, i documenti relativi ad interventi di aggiornamento dei procedimenti amministrativi provinciali, nonché alla predisposizione e alla condivisione di formulari di atti e modulistica finalizzati a favorire la partecipazione e lo snellimento delle procedure amministrative.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 9, 0, 'Controlli e verifiche esterne', 'Si classificano in questa posizione i documenti relativi alle verifiche e ai controlli esterni di cui siano oggetto strutture della Provincia e la loro attività a prescindere dall’autorità che li eserciti (Corte dei conti, Guardia di Finanza, Procura della Repubblica ecc.).
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 10, 0, 'Relazioni e accordi con altri soggetti, rapporti istituzionali', 'Si classificano in questa posizione i documenti relativi a relazioni e accordi con altri soggetti, ai rapporti istituzionali di alto livello e di carattere generale, anche internazionali, intrattenuti direttamente dalla Provincia.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 11, 0, 'Designazione e partecipazione in organi istituzionali esterni', 'Si classificano in questa posizione i documenti relativi alla designazione e alla partecipazione di personale della Provincia ad organismi esterni (Conferenza Stato-Regioni/Province Autonome, Conferenza dei Presidenti delle Regioni, Conferenza permanente per i rapporti tra la Provincia e le autonomie locali, organismi di cooperazione transfrontaliera ecc.), nonché al loro funzionamento, per quanto di competenza provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 12, 0, 'Istituzione e funzionamento di aziende, agenzie, enti strumentali e società di capitali', 'Si classificano in questa posizione i documenti relativi all’istituzione e al funzionamento generale di aziende, agenzie ed enti strumentali funzionali della Provincia. Si classificano, altresì, in questa posizione i documenti relativi alla costituzione e al funzionamento di società e altri enti a partecipazione provinciale. Si classificano in questa posizione inoltre i documenti relativi alle nomine di personale provinciale negli organi di governo di dette strutture.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 13, 0, 'Relazioni sindacali e contrattazione', 'Si classificano in questa posizione i documenti relativi ai rapporti tra la Provincia, le organizzazioni sindacali e le rappresentanze del personale. Si classificano inoltre in questa posizione i documenti relativi all’elezione dei rappresentanti sindacali del personale provinciale, agli accordi sindacali sottoscritti tra le organizzazioni di categoria e la Provincia e alle relazioni sugli incontri relativi alla stipula di detti accordi. Si classificano in questa posizione i documenti relativi alla convocazione e all’autorizzazione di assemblee sindacali. NOTA bene – I documenti relativi a questioni specifiche inerenti alla gestione di aspetti che interessano la posizione di soggetti singoli si classificano nel titolo 4 e relative classi e sottoclassi. I documenti relativi all’attività svolta dalla Provincia in sede di contrattazione sindacale per il personale sanitario si classificano nella classe 22.11 e relative sottoclassi, per il personale del comparto scolastico nella classe 26.5.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 14, 0, 'Comunicazione, editoria e attività informativo-promozionale', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 14, 1, 'Comunicazione istituzionale', 'Si classificano in questa posizione i documenti relativi all’attività di comunicazione e
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 14, 2, 'Editoria e produzioni audiovisive', 'Si classificano in questa posizione i documenti relativi all’attività editoriale e di produzione di materiale audiovisivo, nonché alle attività di vendita e distribuzione di pubblicazioni edite dalla Provincia, ivi comprese le richieste di acquisto di copie ed eventuali comunicazioni a clienti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 14, 3, 'Pubblicità, promozione e campagne informative', 'Si classificano in questa posizione i documenti relativi alla pubblicità, alla attività di promozione e alle campagne informative, comprese le guide ai servizi forniti al cittadino. Si classificano in questa posizione i documenti relativi ad iniziative generali e/o settoriali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 15, 0, 'Convegni, manifestazioni ed eventi culturali', 'Si classificano in questa posizione i documenti relativi all’organizzazione di convegni, manifestazioni ed eventi culturali curati o patrocinati dalle strutture provinciali. Si
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 16, 0, 'Minoranze linguistiche 1.16.1 Tutela e promozione', 'Si classificano in questa posizione i documenti relativi alla tutela delle minoranze linguistiche, nonché quelli relativi ad attività di promozione di queste realtà. Si classificano in questa posizione i documenti relativi alla tutela di tali minoranze nell’ambito del sistema educativo provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 16, 2, 'Accertamenti linguistici', 'Si classificano in questa posizione i documenti relativi all’accertamento della conoscenza delle lingue delle minoranze ladina, mochena e cimbra, ivi compresi gli attestati di conoscenza linguistica.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 17, 0, 'Riconoscimento delle persone giuridiche private', 'Si classificano in questa posizione i documenti relativi alla gestione del registro delle persone giuridiche private.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (1, 18, 0, 'Pari opportunità', 'Si classificano in questa posizione i documenti relativi agli interventi in materia di pari opportunità, ivi compresi quelli relativi ai contributi erogati per progetti di soggetti terzi. Si classificano in questa posizione i documenti relativi all’attività del consigliere/a di parità e all’attività della commissione di pari opportunità.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (2, 1, 0, 'Presidente della Provincia', 'Si classificano in questa posizione i documenti relativi al Presidente (elezione, attribuzioni, cessazione dalla carica ecc.) e i documenti relativi alle funzioni da lui svolte nel governo della Provincia (rappresentanza legale, deleghe di funzioni e di firma ecc.).
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (2, 2, 0, 'Giunta, assessori e vicepresidente', 'Si classificano in questa posizione i documenti relativi agli assessori, al vicepresidente e alla Giunta provinciale (nomina, attribuzioni, cessazione della carica, definizione delle indennità ecc.) e i documenti relativi alle funzioni da essi svolte nel governo della Provincia. Si classificano in questa posizione anche i documenti relativi all’attività amministrativa a supporto della Giunta (ordini del giorno, verbali delle adunanze). I provvedimenti emanati vengono gestiti in doppio esemplare, uno organizzato in serie, l’altro classificato nella classe di pertinenza. Si classificano in questa posizione i documenti relativi alla fase istruttoria e di perfezionamento delle deliberazioni di Giunta, quali le lettere relative alla trasmissione delle proposte di deliberazioni inviate dai servizi, nonché tutti i documenti relativi agli aspetti procedurali connessi all’iter deliberativo, ivi compresi i pareri resi dalle strutture di staff.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (2, 3, 0, 'Segretario generale e dirigenza', 'Si classificano in questa posizione i documenti relativi alla nomina, alle attribuzioni nonché alla cessazione dalla carica delle figure in oggetto; si classificano in questa posizione, inoltre, i documenti relativi alle funzioni di coordinamento generale da esse svolte nel governo della Provincia. Si classificano, altresì, in questa posizione i documenti relativi alla fase istruttoria e di perfezionamento delle determinazioni dirigenziali (documenti relativi alla trasmissione e alla restituzione delle proposte di determinazione dirigenziale avanzate dalle strutture ecc), nonché tutti i documenti relativi ai connessi aspetti procedurali. Si classificano in questa posizione i documenti relativi al funzionamento degli organi monocratici e alla relativa serie dei pareri.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (2, 4, 0, 'Comitati e commissioni settoriali e intersettoriali', 'Si classificano in questa posizione i documenti relativi alla istituzione e al funzionamento (nomine, convocazioni, ordini del giorno, verbali delle riunioni ecc.) di comitati, commissioni, gruppi di lavoro, conferenze dei servizi e altri organi consultivi, sia settoriali che intersettoriali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (2, 5, 0, 'Consiglio provinciale', 'Si classificano in questa posizione i documenti relativi all’attività di funzionamento del Consiglio (convocazione, comunicazioni al Consiglio, ordini del giorno delle sedute, programmi dei lavori consiliari ecc.), nonché quelli relativi all’attività di controllo politico (interrogazioni, interpellanze, mozioni).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (2, 6, 0, 'Organi delle agenzie provinciali', 'Si classificano in questa posizione i documenti relativi alla costituzione, alla convocazione e al funzionamento degli organi delle agenzie provinciali, quali il consiglio di amministrazione, il collegio dei revisori dei conti, gli organi collegiali o tecnico-scientifici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 1, 0, 'Beni immobili e demanio provinciale', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 1, 1, 'Acquisizione e destinazione d’uso', 'Si classificano in questa posizione i documenti relativi all’acquisizione, anche tramite
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 1, 2, 'Locazione, comodato, concessione e servitù', 'Si classificano in questa posizione i documenti relativi alla gestione patrimoniale dei beni immobili nella disponibilità della Provincia, quali locazioni, tanto attive che passive, concessioni in uso, comodati, servitù attive e/o passive ecc.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 1, 3, 'Alienazione, cessione e permuta', 'Si classificano in questa posizione i documenti relativi ad alienazione, cessione in proprietà a titolo oneroso o gratuito, permuta o trasferimento a titolo gratuito agli enti locali o ad altri enti pubblici di beni immobili della Provincia; si classificano in questa posizione, inoltre, i documenti relativi a procedure di espropriazione passiva.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 1, 4, 'Inventario patrimoniale, stima e consistenza', 'Si classificano in questa posizione i documenti relativi alla gestione dell’inventario dei beni immobili della Provincia, dalla registrazione alla dismissione, ivi compresa l’attività di ricognizione periodica sui beni. Si classificano in questa posizione i documenti relativi ai titoli di possesso dei beni immobili provinciali, nonché quelli relativi a confinazioni e delimitazioni. Si classificano, inoltre, in questa posizione i documenti relativi alle stime condotte su beni immobili, comprese quelle effettuate per conto terzi.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 2, 0, 'Beni mobili e relativi servizi', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 2, 1, 'Acquisizione, fornitura e manutenzione', 'Si classificano in questa posizione i documenti relativi all’acquisizione, anche tramite
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 2, 2, 'Locazione e comodato', 'Si classificano in questa posizione i documenti relativi all’attività di gestione dei beni mobili, quali locazioni attive o passive, concessioni, assegnazioni in comodato, noleggio ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 2, 3, 'Alienazione, cessione e permuta', 'Si classificano in questa posizione i documenti relativi ad alienazioni, cessioni e permute dei beni mobili di proprietà provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 2, 4, 'Inventario patrimoniale, consistenza e stima', 'Si classificano in questa posizione i documenti relativi alla gestione dell’inventario dei beni mobili patrimoniali, dalla registrazione alla dismissione, ivi comprese l’assegnazione ai consegnatari e l’attività di ricognizione periodica sui beni.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 3, 0, 'Organizzazione logistica', 'Si classificano in questa posizione i documenti relativi all’organizzazione logistica provinciale, ivi comprese le attività di programmazione, gestione delle sedi, assegnazione ed allestimento degli spazi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 0, 'Servizi generali, ausiliari e utenze collegate', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 1, 'Ristorazione e spacci interni', 'Si classificano in questa posizione i documenti relativi alla gestione dei servizi di
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 2, 'Vigilanza e portineria', 'Si classificano in questa posizione i documenti relativi alla gestione del servizio di vigilanza e di portierato.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 3, 'Pulizia, gestione dei rifiuti, traslochi e facchinaggio', 'Si classificano in questa posizione i documenti relativi alla gestione, diretta o indiretta, dei servizi di pulizia, di trasloco e/o facchinaggio, comprese le richieste di intervento nelle sedi provinciali, nonché i documenti relativi alla gestione dei rifiuti e della raccolta differenziata nelle stesse.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 4, 'Servizio autisti e autorimessa', 'Si classificano in questa posizione i documenti relativi alla gestione del servizio autisti, quali, ad esempio, richieste e comunicazioni di disponibilità di automezzi interni ed esterni e fogli di viaggio.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 5, 'Posta interna, centralino e centro duplicazioni', 'Si classificano in questa posizione i documenti relativi alla gestione dei servizi di posta interna, di centralino telefonico e del centro duplicazioni. Si classificano, altresì, in questa posizione i documenti relativi alla gestione e all’aggiornamento dell’elenco telefonico interno.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 6, 'Beni di facile e rapido consumo', 'Si classificano in questa posizione i documenti relativi all’acquisizione e alla gestione di beni di facile e rapido consumo, quali vestiario, divise, cancelleria e materiale informatico (toner, cartucce per stampanti ecc.). Si classificano in questa posizione i documenti relativi alla gestione dei relativi buoni d’ordine e dei registri di carico/scarico dei beni.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 7, 'Utenze, telefonia e spese di funzionamento', 'Si classificano in questa posizione i documenti relativi alla gestione delle spese di funzionamento (carburanti, spese condominiali ecc.) e delle utenze di cui la Provincia è titolare (elettricità, acqua, luce, gas, telefonia fissa e mobile, abbonamenti radiotelevisivi ecc.). Si classificano, altresì, in questa posizione i documenti relativi all’attivazione, disattivazione, configurazione ecc. delle reti di telefonia delle strutture provinciali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 4, 8, 'Contratti assicurativi', 'Si classificano in questa posizione i documenti relativi alla stipula, al rinnovo e allo spegnimento di polizze assicurative da parte della Provincia a prescindere dal bene assicurato (beni immobili, beni mobili, autoveicoli ecc.).
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (3, 5, 0, 'Attività negoziale e contrattuale', 'Si classificano in questa posizione i documenti relativi all’istruzione e alla gestione dell’attività negoziale e contrattuale (appalti, convenzioni, gare, cottimi ecc.), ivi compresa quella connessa all’affidamento di incarichi temporanei per alte professionalità e, più in generale, di incarichi di studio, ricerca, consulenza e collaborazione. Si classificano in questa posizione i documenti relativi alla gestione dell’Anagrafe degli incarichi e delle consulenze esterne. Si classificano in questa posizione anche i documenti relativi all’attività in materia negoziale e contrattuale svolta per conto terzi (assistenza e consulenza in materia di appalti di lavori, di servizi e di forniture ecc.) nonché quelli relativi alla progettazione e gestione di strumenti di e-procurement.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 1, 0, 'Concorsi e selezioni', 'Si classificano in questa posizione i documenti relativi alla pubblicazione dei bandi di concorso e di selezione indetti per l’assunzione di personale provinciale, nonché i documenti relativi alla gestione dei procedimenti concorsuali, ivi comprese le procedure per progressioni verticali e orizzontali. Si classificano in questa posizione i documenti relativi alla formazione e alla gestione delle graduatorie del personale docente. Si classificano inoltre in questa posizione i documenti relativi alle domande di assunzione, di incarico, le offerte di collaborazione e i relativi curricula trasmessi anche in assenza di procedure concorsuali. NOTA bene – I documenti relativi ai bandi di concorsi e selezioni trasmessi da altri enti si classificano nella classe 6.2.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 2, 0, 'Assunzioni e cessazioni dal rapporto di lavoro', 'Si classificano in questa posizione i documenti relativi all’assunzione del personale della Provincia a tempo determinato o indeterminato, ivi compresi quelli relativi al periodo di prova dei dipendenti e alle conseguenti valutazioni e pareri. Si classificano, altresì, in questa posizione i documenti relativi alla cessazione dei rapporti lavorativi. Si classificano in questa posizione, inoltre, i documenti relativi alle comunicazioni inerenti ai dati personali dei dipendenti e alle relative variazioni (certificati di residenza, del casellario giudiziale, titoli di studio, attestati di qualifica ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 3, 0, 'Carriera e stato giuridico-economico', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 3, 1, 'Attribuzione di incarichi, funzioni, ordini e rapporti di servizio', 'Si classificano in questa posizione sia i documenti relativi agli ordini e ai rapporti di
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 3, 2, 'Inquadramenti e mansioni', 'Si classificano in questa posizione i documenti relativi all’inquadramento e alla gestione del conferimento di mansioni, anche superiori, ai dipendenti, nonché i documenti relativi all’aggiornamento dell’albo della dirigenza e dell’albo dei direttori.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 3, 3, 'Rapporti di lavoro a tempo parziale', 'Si classificano in questa posizione i documenti relativi all’applicazione di forme contrattuali a tempo parziale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 3, 4, 'Telelavoro', 'Si classificano in questa posizione i documenti relativi all’applicazione della forma contrattuale flessibile del telelavoro.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 4, 0, 'Retribuzione e compensi', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 4, 1, 'Trattamenti retributivi fissi e accessori', 'Si classificano in questa posizione i documenti relativi ai trattamenti retributivi fissi e
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 4, 2, 'Trattamenti di missione', 'Si classificano in questa posizione i documenti relativi al rimborso delle spese sostenute per missioni svolte a nome dell’ente o in sua rappresentanza. Si classificano in questa posizione i documenti relativi alle richieste di utilizzo di automezzo proprio per
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 4, 3, 'Trattenute diverse', 'Si classificano in questa posizione i documenti relativi alle trattenute di natura non fiscale o pensionistica effettuate sulla retribuzione del dipendente, quali cessioni di quote dello stipendio, gestione delle trattenute del credito INPDAP, pignoramenti, assegni al coniuge ecc. Si classificano in questa posizione anche i documenti relativi alle trattenute sindacali, alle trattenute a favore di forme associative (circoli ricreativi ecc.), nonché alle ritenute per scioperi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 4, 4, 'Assegno per il nucleo familiare', 'Si classificano in questa posizione i documenti relativi alla corresponsione e alla gestione degli assegni per il nucleo familiare.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 5, 0, 'Adempimenti fiscali, previdenziali, assistenziali e assicurativi 4.5.1 Adempimenti fiscali', 'Si classificano in questa posizione i documenti relativi alla gestione degli adempimenti tributari e delle detrazioni fiscali operate per conto del personale provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 5, 2, 'Posizioni previdenziali, assistenziali e assicurative', 'Si classificano in questa posizione i documenti relativi alla gestione degli adempimenti previdenziali, assistenziali e assicurativi, obbligatori e integrativi, a favore del personale dipendente. Si classificano in questa posizione anche i documenti relativi alla stipula di assicurazioni di tipo volontario da parte dei dipendenti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 5, 3, 'Trattamenti di fine rapporto, indennità premio servizio e trattamenti di pensione', 'Si classificano in questa posizione i documenti relativi al riconoscimento di periodi previdenziali, ivi compresi ricongiunzioni e riscatti, ai fini della determinazione dei trattamenti di fine rapporto e dei trattamenti pensionistici. Si classificano in questa posizione i documenti relativi alla gestione dell’erogazione degli anticipi del TFR e del credito INPDAP.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 6, 0, 'Presenze e assenze', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 6, 1, 'Orario di lavoro', 'Si classificano in questa posizione i documenti relativi alla rilevazione delle presenze, al controllo delle assenze e al rispetto dell’orario di lavoro da parte del personale provinciale, ivi compresi gli elenchi riepilogativi mensili. Si classificano, altresì, in questa posizione i documenti relativi a reperibilità, ore di straordinario, recuperi, rilevazione di saldo negativo, spostamenti di giornate dei part-time ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 6, 2, 'Ferie, congedi, aspettative, permessi e distacchi', 'Si classificano in questa posizione i documenti relativi a ferie, congedi, aspettative, distacchi e permessi, a prescindere dalle motivazioni (150 ore, permessi sindacali ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 7, 0, 'Comandi, messa a disposizione e mobilità esterna', 'Si classificano in questa posizione i documenti relativi al personale provinciale comandato presso altri enti e viceversa. Si classificano, altresì, in questa posizione i documenti relativi alla mobilità esterna del personale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 8, 0, 'Trasferimenti', 'Si classificano in questa posizione i documenti relativi alla gestione dei trasferimenti e della mobilità interna del personale, ivi compreso quello del comparto scolastico.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 9, 0, 'Attività ispettiva e disciplinare', 'Si classificano in questa posizione i documenti relativi all’attività ispettiva e para-ispettiva svolta nei confronti del personale, quali il controllo sulla malattia e sull’utilizzo dei buoni pasto. Si classificano in questa posizione i documenti relativi ai procedimenti disciplinari e ai provvedimenti presi a carico dei dipendenti, nonché i documenti relativi all’applicazione del
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 10, 0, 'Attività di conciliazione', 'Si classificano in questa posizione i documenti relativi alle controversie inerenti al rapporto di lavoro gestite in sede interna di conciliazione. Si classificano in particolare in questa posizione i documenti relativi alla fase pre-contenziosa della controversia con il dipendente, ivi compresi ricorsi, tentativi di conciliazione e i conseguenti verbali di conciliazione o mancata conciliazione.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 11, 0, 'Valutazione del personale', 'Si classificano in questa posizione i documenti relativi alla valutazione dei dipendenti provinciali, compreso il personale dirigenziale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 12, 0, 'Formazione e aggiornamento del personale', 'Si classificano in questa posizione i documenti relativi all’organizzazione di attività formativa per il personale provinciale e alla partecipazione ad iniziative di formazione e aggiornamento, anche esterne, da parte dello stesso.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 13, 0, 'Servizi al personale', 'Si classificano in questa posizione i documenti relativi ai servizi che la Provincia fornisce ai dipendenti (buoni pasto ecc.).
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 14, 0, 'Stage e tirocini formativi', 'Si classificano in questa posizione i documenti relativi all’effettuazione di stage e tirocini, ivi comprese le relative convenzioni con soggetti terzi. Si classificano inoltre in questa posizione i documenti relativi volontari del Servizio civile impiegati presso la Provincia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 15, 0, 'Sicurezza sul lavoro', 'Si classificano in questa posizione i documenti relativi all’attività svolta dalla Provincia in materia di sicurezza sul lavoro. Si classificano in questa posizione tanto i documenti relativi alle ordinarie e periodiche iniziative di tutela della salute e sorveglianza sanitaria nei confronti dei singoli dipendenti (visite mediche periodiche per video-operatori e per addetti a laboratori comportanti rischi di varia natura), quanto quelli relativi al monitoraggio della sicurezza negli ambienti di lavoro, alla programmazione, alle segnalazioni e all’attuazione degli interventi diretti a tal fine. Si classificano, inoltre, in questa posizione i documenti relativi all’attività del Nucleo di prevenzione e protezione. Si classificano in questa posizione anche i documenti relativi ai certificati rilasciati alla Provincia ad attestazione della conformità alla vigente normativa antincendi di opere e impianti di sua pertinenza.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 16, 0, 'Stato di salute del dipendente', 'Si classificano in questa posizione i documenti relativi allo stato di salute del dipendente, quali certificati medici, documenti relativi a visite mediche specialistiche per gravi patologie, a esiti di visite mediche legali, visite fiscali e in generale tutti quelli relativi allo stato di malattia, infermità o infortunio del dipendente. In relazione a quest’ultimo ambito si
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (4, 17, 0, 'Pari opportunità del personale provinciale', 'Si classificano in questa posizione i documenti relativi alle attività del comitato per le pari opportunità e, in particolare, quelli relativi alle segnalazioni dei singoli dipendenti della Provincia.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 1, 0, 'Bilancio di previsione', 'Si classificano in questa posizione i documenti relativi alla predisposizione del bilancio preventivo della Provincia e alla definizione e gestione dei budget assegnati alle strutture; si classificano, altresì, in questa posizione i documenti relativi alle operazioni connesse all’assestamento e alle variazioni del bilancio preventivo, nonché agli adempimenti connessi al rispetto del patto di stabilità.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 2, 0, 'Rendiconto generale', 'Si classificano in questa posizione i documenti relativi alla predisposizione del rendiconto finanziario generale (conto consuntivo), ivi compresi quelli relativi al calcolo dei residui attivi e passivi e alle perenzioni. Si classificano in questa posizione i documenti relativi alla predisposizione del conto generale del patrimonio.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 3, 0, 'Gestione del bilancio 5.3.1 Entrate', 'Si classificano in questa posizione i documenti relativi alla gestione delle entrate spettanti alla Provincia, a prescindere dalla loro origine. Si classificano in questa posizione i documenti relativi alle contribuzioni e alle entrate provenienti da affitti e locazioni di beni provinciali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 3, 2, 'Spese', 'Si classificano in questa posizione i documenti relativi alle diverse fasi procedurali (impegni, liquidazioni, ordinazioni ecc.) di ogni genere di spesa (spese correnti, in conto capitale, per rimborso di prestiti, per servizi per conto di terzi). Si classificano, in particolare, in questa posizione i documenti relativi a liquidazioni, mandati di pagamento e relativi elenchi, verifiche a campione degli atti di spesa, gestione dell’albo dei beneficiari di provvidenze di natura economica, nonché quelli relativi all’istituzione e gestione di contabilità speciali e alle anticipazioni di cassa.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 4, 0, 'Gestione del debito', 'Si classificano in questa posizione i documenti relativi alla gestione delle varie forme d’indebitamento della Provincia (mutui, prestiti obbligazionari ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 5, 0, 'Fiscalità attiva', 'Si classificano in questa posizione i documenti relativi alla gestione dei diversi cespiti riscossi dalla Provincia (IRAP, bollo auto, tassa per l’abilitazione all’esercizio professionale, tassa per il diritto allo studio universitario, imposta provinciale di trascrizione, tributo speciale per lo smaltimento dei rifiuti solidi, addizionale provinciale all’imposta sul consumo di energia elettrica, addizionale IRPEF ecc.), ivi compresi quelli relativi a operazioni di rimborso e ricorsi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 6, 0, 'Fiscalità passiva', 'Si classificano in questa posizione i documenti relativi agli adempimenti cui la Provincia è tenuta in quanto soggetto fiscale, come ad esempio i documenti relativi alla corresponsione
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 7, 0, 'Risorse da trasferimenti comunitari, compartecipazioni erariali, regionali, provinciali e finanza locale', 'Si classificano in questa posizione i documenti relativi al riparto e al trasferimento alla Provincia, alle autonomie locali o ad altri enti (Camera di Commercio ecc.) delle risorse derivanti da trasferimenti comunitari, compartecipazioni erariali, regionali e provinciali, compresi i relativi rendiconti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 8, 0, 'Tesoreria e istituti di credito', 'Si classificano in questa posizione i documenti relativi ai servizi forniti dall’istituto tesoriere, compresi i rapporti di conto corrente bancario, la gestione della cassa, la gestione delle carte di credito e di altri strumenti di pagamento. Si classificano in questa posizione i documenti relativi all’attività di monitoraggio e gestione della liquidità. Si classificano, inoltre, in questa posizione i documenti relativi al convenzionamento con istituti di credito e con gli enti di garanzia cui è demandata l’erogazione di contributi provinciali.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 9, 0, 'Cassa economato centrale', 'Si classificano in questa posizione i documenti relativi al servizio di gestione di cassa economato centrale.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 10, 0, 'Agenti contabili', 'Si classificano in questa posizione i documenti relativi all’attività degli agenti contabili.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 11, 0, 'Funzionari delegati', 'Si classificano in questa posizione i documenti relativi all’attività dei funzionari delegati, quali atti di liquidazione, atti di nomina e revoca, verbali di verifica e passaggio di gestione, libri giornale e rendicontazioni.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (5, 12, 0, 'Partecipazioni finanziarie', 'Si classificano in questa posizione i documenti relativi alla gestione finanziaria delle partecipazioni della Provincia a società di capitali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 1, 0, 'Sistema archivistico provinciale 6.1.1 Archivi correnti', 'Si classificano in questa posizione i documenti relativi all’organizzazione del sistema di gestione documentaria nella sua fase corrente. Si classificano quindi in questa posizione i documenti relativi all’adozione e all’applicazione di strumenti di gestione, quali il protocollo della corrispondenza, il titolario di classificazione, i repertori dei fascicoli, alla definizione del massimario di selezione, nonché all’attività di sorveglianza operata nei confronti degli archivi correnti delle strutture provinciali. Si classificano in questa posizione anche i documenti relativi alle operazioni di ricezione, smistamento e spedizione della posta. Si classificano, altresì, in questa posizione le ricevute di consegna ai competenti servizi dei documenti presentati agli sportelli periferici, nonché i documenti relativi alla trasmissione della corrispondenza pervenuta a strutture provinciali non competenti alla trattazione dell’affare.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 1, 2, 'Archivi di deposito e storico', 'Si classificano in questa posizione i documenti relativi all’organizzazione e alla gestione degli archivi di deposito e storico, nonché all’attività di sorveglianza operata nei confronti degli archivi di deposito delle strutture provinciali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 1, 3, 'Consistenza del materiale archivistico', 'Si classificano in questa posizione i documenti relativi a scarti, versamenti, trasferimenti, acquisti, doni e legati, recupero di materiale archivistico d’interesse provinciale, depositi, segnalazioni di furti e più in generale a tutte le operazioni destinate ad incidere sulla consistenza del materiale archivistico della Provincia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 1, 4, 'Lavori storico-archivistici', 'Si classificano in questa posizione i documenti relativi all’attività di ordinamento, inventariazione, censimento e ad altre forme di lavori storico-archivistici condotte sui fondi documentari conservati presso l’Archivio provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 1, 5, 'Interventi conservativi, di restauro e riproduzione', 'Si classificano in questa posizione i documenti relativi alle attività di tutela volte ad assicurare la corretta conservazione del materiale archivistico concservato presso l’Archivio provinciale, quali interventi di restauro, disinfezione e disinfestazione, riproduzione.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 1, 6, 'Consultazione, copie e ricerche', 'Si classificano in questa posizione i documenti relativi alla consultazione di documenti conservati negli archivi provinciali da parte di privati, amministrazioni ed enti. Si classificano, inoltre, in questa posizione i documenti relativi all’attività di copia dei documenti e alle relative tariffe. Si classificano in questa posizione anche i documenti relativi alla gestione della sala di studio dell’Archivio provinciale.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 2, 0, 'Albo provinciale', 'Si classificano in questa posizione i documenti relativi all’attività di affissione e pubblicazione all’Albo ufficiale della Provincia, nonché all’attività di notificazione condotta dal personale provinciale. Si classificano, altresì, in questa posizione i documenti relativi alle comunicazioni di avvisi di bandi di concorsi e selezioni trasmessi da altri enti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 3, 0, 'Accesso ai documenti amministrativi', 'Si classificano in questa posizione i documenti relativi all’attività della Provincia volta a garantire l’esercizio dei diritti d’informazione, accesso e partecipazione al procedimento amministrativo, nonché alle singole richieste di esercizio del diritto d’accesso alla documentazione amministrativa.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 4, 0, 'Protezione dei dati personali e segreto statistico', 'Si classificano in questa posizione i documenti relativi alla protezione e alla verifica dei dati personali, nonché all’applicazione delle disposizioni in materia di segreto statistico. Si classificano in questa posizione anche i documenti relativi all’individuazione dei responsabili di questa attività, nonché agli adempimenti in materia nei confronti dell’autorità garante e del difensore civico provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 5, 0, 'Statistica', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 5, 1, 'Produzione di dati statistici', 'Si classificano in questa posizione i documenti relativi all’attività di programmazione
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 5, 2, 'Analisi statistiche', 'Si classificano in questa posizione i documenti relativi allo studio e al trattamento dei dati statistici e alla produzione di elaborazioni statistiche.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 5, 3, 'Ricerche statistiche', 'Si classificano in questa posizione i documenti relativi all’elaborazione di modelli interpretativi dei dati statistici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 6, 0, 'Servizi e sistemi informatici e di comunicazione', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 6, 1, 'Attività informatica di base e applicativi', 'Si classificano in questa posizione i documenti relativi alla gestione delle attività
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 6, 2, 'Attività informatica infrastrutturale', 'Si classificano in questa posizione i documenti relativi all’attività di progettazione, coordinamento e sviluppo d’infrastrutture di rete, quali cablatura e estensione delle reti di fibra ottica delle sedi delle strutture provinciali.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 6, 3, 'Sistemi informativi e informatici', 'Si classificano in questa posizione i documenti relativi alla progettazione e alla gestione dei sistemi informativi informatizzati settoriali e intersettoriali della Provincia. Si classificano, in particolare, in questa posizione i documenti relativi alla gestione degli accessi alle banche dati e ai sistemi informativi provinciali ed extra provinciali (PRA, ICEF, OPEN KAT, Sistema informativo statistico ecc.), nonché quelli inerenti al documento programmatico per la sicurezza. Si classificano in questa posizione, inoltre, i documenti relativi alla realizzazione e alla manutenzione dei siti web della Provincia e delle strutture.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 7, 0, 'Reti di telecomunicazione', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 7, 1, 'Impianti e frequenze radiotelevisive', 'Si classificano in questa posizione i documenti relativi a realizzazione, gestione e
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 7, 2, 'Rete radiomobile provinciale', 'Si classificano in questa posizione i documenti relativi alla realizzazione e alla gestione del sistema di telecomunicazioni per radiomobile provinciale. Si classificano in questa posizione anche i documenti relativi alle autorizzazioni per l’utilizzo delle frequenze e alla distribuzione degli apparecchi ricetrasmittenti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 7, 3, 'Rete di comunicazione elettronica', 'Si classificano in questa posizione i documenti relativi a pianificazione, progettazione, realizzazione e gestione delle infrastrutture e degli impianti di comunicazione elettronica (banda larga, reti in fibra ottica, reti wireless ecc.), gestite direttamente dalla Provincia o da essa delegate ad altri soggetti.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (6, 8, 0, 'Biblioteche di struttura, centri di documentazione e Archivio fotografico storico', 'Si classificano in questa posizione i documenti relativi all’organizzazione, gestione e funzionamento delle biblioteche di struttura, dei centri di documentazione operanti in ambito provinciale e dell’Archivio fotografico storico. Si classificano in questa posizione i documenti relativi all’acquisizione e alla catalogazione di materiale bibliografico, emerografico, fotografico e documentale (comprese gazzette ufficiali, bollettini ufficiali della Regione ecc.), nonché quelli relativi all’organizzazione e al funzionamento del servizio di prestito, consultazione e riproduzione.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 1, 0, 'Affari legali', 'Si classificano in questa posizione i documenti relativi agli affari legali in genere e alla definizione pregiudiziale delle controversie in sede di conciliazione. Si classificano, altresì, in questa posizione i documenti relativi a procedure esecutive (pignoramenti, procedure concorsuali ecc.), nonché alle pratiche di rimborso per le spese legali. Si classificano inoltre in questa posizione i documenti relativi a richieste di informazioni presentate alla Provincia da organismi esterni (procure della Repubblica ecc.) ed aventi ad oggetto l’attività di soggetti terzi (ditte fallite ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 0, 'Contenzioso', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 1, 'Contenzioso amministrativo', 'Si classificano in questa posizione i documenti relativi al contenzioso amministrativo,
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 2, 'Contenzioso civile', 'Si classificano in questa posizione i documenti relativi al contenzioso civile definito nelle sedi giurisdizionali competenti (Giudice di pace, Tribunale, Corte di appello, Corte di cassazione civile, Commissario degli usi civici ecc.), che veda la Provincia nelle vesti di attore o convenuto.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 3, 'Contenzioso del lavoro', 'Si classificano in questa posizione i documenti relativi alle controversie civili in materia di lavoro o nelle materie regolate dal processo del lavoro, che vedano la Provincia nelle vesti di attore o di convenuto, a prescindere dalla sede di dibattimento (Tribunale del giudice del lavoro, Corte d’appello, Corte di cassazione ecc.).
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 4, 'Contenzioso tributario', 'Si classificano in questa posizione i documenti relativi al contenzioso tributario coinvolgente a qualsiasi titolo la Provincia, dibattuto dinanzi alle Commissioni tributarie di ogni ordine e grado.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 5, 'Contenzioso penale', 'Si classificano in questa posizione i documenti relativi ai procedimenti penali dibattuti nelle diverse sedi giurisdizionali (Giudice di pace, Tribunale, Corte d’assise, Corte di appello, Corte d’assise di appello, Corte di cassazione penale ecc.), che vedano la Provincia nelle vesti di attore o convenuto.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 6, 'Ricorsi straordinari al Presidente della Repubblica', 'Si classificano in questa posizione i documenti relativi ai ricorsi straordinari presentati al Presidente della Repubblica, coinvolgenti a qualsiasi titolo la Provincia,.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 7, 'Contenzioso dinanzi alla Corte costituzionale', 'Si classificano in questa posizione i documenti relativi alle questioni dibattute dinanzi alla Corte costituzionale coinvolgenti la Provincia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 2, 8, 'Contenzioso contabile', 'Si classificano in questa posizione i documenti relativi ai procedimenti di responsabilità contabile e amministrativa dibattuti dinanzi alla Corte dei conti coinvolgenti la Provincia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 3, 0, 'Pareri e consulenze legali', 'Si classificano in questa posizione i documenti relativi a pareri e consulenze legali resi a uffici e personale della Provincia.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (7, 4, 0, 'Risarcimenti per responsabilità civile e patrimoniale', 'Si classificano in questa posizione i documenti relativi alla gestione delle richieste di risarcimento per responsabilità civile e patrimoniale presentate dalla o alla Provincia, anche in caso d’incidenti con danni arrecati a terzi, fatti salvi i documenti relativi ad eventuali contenziosi processuali, da ricondurre alle classi di pertinenza. Si classificano, altresì, in questa posizione i documenti relativi alle responsabilità di terzi che abbiano cagionato infortuni a dipendenti provinciali, costretti per questo ad assentarsi dal servizio.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 1, 0, 'Programmazione provinciale', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 1, 1, 'Programma di sviluppo provinciale', 'Si classificano in questa posizione i documenti relativi alla definizione del programma
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 1, 2, 'Programma di gestione', 'Si classificano in questa posizione i documenti relativi all’attività di predisposizione, modifica e attuazione del programma di gestione, nonché alla definizione degli obiettivi da esso dipendenti.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 1, 3, 'Programmazione settoriale e intersettoriale', 'Si classificano in questa posizione i documenti relativi alle attività di studio e programmazione connesse all’esercizio di funzioni tanto trasversali quanto settoriali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 1, 4, 'Programmazione negoziata e patti territoriali', 'Si classificano in questa posizione i documenti relativi alla definizione di accordi di programma e intese istituzionali raggiunti dalla Provincia con altri soggetti pubblici o privati, ivi compresi i patti territoriali e i finanziamenti statali derivanti da specifici accordi. Si classificano, inoltre, in questa posizione i documenti relativi agli accordi di programma per lo sviluppo delle aree montane.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 2, 0, 'Programmazione comunitaria e affari europei 8.2.1 Fondi strutturali', 'Si classificano in questa posizione i documenti relativi all’attività generale di programmazione e a quella di gestione di fondi strutturali comunitari (FESR, FSE ecc.), nonché i documenti relativi ai programmi ad essi connessi (Interreg, Spazio alpino ecc.). Si classificano, inoltre, in questa posizione i documenti relativi alla programmazione degli specifici obiettivi in merito alla gestione dei fondi strutturali (DOCUP, QCS ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 2, 2, 'Programmi d’iniziativa comunitaria e progetti', 'Si classificano in questa posizione i documenti relativi a progetti d’iniziativa comunitaria gestiti direttamente dalla Provincia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 2, 3, 'Monitoraggio e valutazione dei risultati', 'Si classificano in questa posizione i documenti relativi all’attività pluriennale di monitoraggio e valutazione dei risultati di applicazione dei diversi interventi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 3, 0, 'Coordinamento, controllo interno e sistema qualità', 'Si classificano in questa posizione i documenti relativi al coordinamento dell’attività delle strutture provinciali, esplicato ad esempio attraverso riunioni di servizio o cabine di regia, e del relativo controllo interno, ivi compreso il controllo di gestione. Si classificano in questa posizione i documenti relativi alle certificazioni di qualità dei servizi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 0, 'Vigilanza sulle autonomie locali e sugli enti vigilati', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 1, 'Gestione contabile e stato patrimoniale', 'Si classificano in questa posizione i documenti relativi alle attività di consulenza,
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 2, 'Atti amministrativi e organizzazione dell’ente', 'Si classificano in questa posizione i documenti relativi alle attività di consulenza, vigilanza, autorizzazione e controllo svolte dalla Provincia in materia di gestione degli atti amministrativi (regolamenti, atti di organizzazione interna ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 3, 'Organi di gestione', 'Si classificano in questa posizione i documenti relativi alle attività di consulenza, vigilanza, autorizzazione e controllo svolte dalla Provincia in relazione al funzionamento degli organi di gestione degli enti vigilati (nomine, surroghe, commissariamenti ecc.).
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 4, 'Personale degli enti vigilati', 'Si classificano in questa posizione i documenti relativi alle attività di consulenza, vigilanza, autorizzazione e controllo svolte dalla Provincia in materia di gestione del personale (autorizzazione all’assunzione, pareri sulle piante organiche ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 5, 'Entrate proprie, tariffe e tributi degli enti locali', 'Si classificano in questa posizione i documenti relativi ai provvedimenti provinciali concernenti tributi e tariffe di competenza degli enti locali, nonché ai contenziosi in materia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 6, 'Usi civici', 'Si classificano in questa posizione i documenti relativi alle attività di consulenza, vigilanza, autorizzazione e controllo svolte dalla Provincia in materia di usi civici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 7, 'Forme associative per l’esercizio di funzioni e servizi pubblici', 'Si classificano in questa posizione i documenti relativi alle funzioni d’indirizzo e di verifica svolte dalla Provincia in ordine alla gestione di forme associative per l’esercizio di funzioni e servizi (corpi intercomunali di polizia locale ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (8, 4, 8, 'Segretari comunali e comprensoriali', 'Si classificano in questa posizione i documenti relativi alle attività di consulenza, vigilanza, autorizzazione e controllo svolte dalla Provincia in materia di segretari comunali e comprensoriali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 1, 0, 'Locali di pubblico spettacolo 9.1.1 Progetti', 'Si classificano in questa posizione i documenti relativi all’approvazione dei progetti per la costruzione o la sostanziale rinnovazione di teatri o di locali di pubblico spettacolo.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 1, 2, 'Agibilità', 'Si classificano in questa posizione i documenti relativi al rilascio delle licenze di agibilità per l’apertura di un teatro o di un luogo di pubblico spettacolo.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 2, 0, 'Pubblici spettacoli', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 2, 1, 'Spettacoli e trattenimenti pubblici', 'Si classificano in questa posizione i documenti relativi al rilascio delle licenze per lo
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 2, 2, 'Gare e competizioni su strada', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa per gare e competizioni su strada.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 3, 0, 'Pubblici esercizi', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 3, 1, 'Funzioni delegate', 'Si classificano in questa posizione i documenti relativi all’esercizio delle funzioni delegate dalla Provincia ai comuni e alla connessa attività di coordinamento, monitoraggio, supporto e controllo in materia di pubblici esercizi. Si classificano, più in generale, in questa posizione i documenti relativi alle segnalazioni concernenti l’attività dei pubblici esercizi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 3, 2, 'Ricorsi e segnalazioni', 'Si classificano in questa posizione i documenti relativi alle segnalazioni nonché ai ricorsi presentati dinanzi alla Giunta provinciale in materia di pubblici esercizi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 3, 3, 'Marchi di prodotto', 'Si classificano in questa posizione i documenti relativi alle procedure di attribuzione dei marchi di prodotto e all’attività di verifica della sussistenza dei relativi requisiti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 4, 0, 'Licenze di pubblica sicurezza 9.4.1 Agenzie di affari', 'Si classificano in questa posizione i documenti relativi all’esercizio delle attività di agenzie di affari (comunicazioni, dichiarazioni di inizio attività ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 4, 2, 'Commercio preziosi', 'Si classificano in questa posizione i documenti relativi all’esercizio delle attività commercio preziosi (comunicazioni, dichiarazioni di inizio attività ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 4, 3, 'Vendita a domicilio', 'Si classificano in questa posizione i documenti relativi all’esercizio delle attività di vendita a domicilio (comunicazioni, dichiarazioni di inizio attività ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 4, 4, 'Arte fotografica e videoregistrazione', 'Si classificano in questa posizione i documenti relativi all’esercizio delle attività di arte fotografica e video registrazione (comunicazioni, dichiarazioni di inizio attività ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 4, 5, 'Commercio di cose antiche o usate', 'Si classificano in questa posizione i documenti relativi all’esercizio delle attività di commercio di cose antiche o usate (comunicazioni, dichiarazioni di inizio attività ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 5, 0, 'Attività ispettiva e sanzionatoria 9.5.1 Divieto di fumo', 'Si classificano in questa posizione i documenti relativi all’attività ispettiva e sanzionatoria delle violazioni in materia di divieto di fumo.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 5, 2, 'Divieto di alcolici ai minori', 'Si classificano in questa posizione i documenti relativi all’attività ispettiva e sanzionatoria delle violazioni in materia di divieto di somministrazione e vendita di bevande alcoliche ai minori.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 5, 3, 'Competenze diverse', 'Si classificano in questa posizione i documenti relativi all’attività ispettiva e sanzionatoria delle violazioni in materia di polizia amministrativa diverse da quelle relative al divieto di fumo e di somministrazione di bevande alcoliche ai minori.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (9, 6, 0, 'Sicurezza del territorio', 'Si classificano in questa posizione i documenti relativi agli atti d’indirizzo, programmazione e realizzazione del sistema di sicurezza provinciale, ivi comprese le provvidenze a favore dei corpi intercomunali di polizia locale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 1, 0, 'Agevolazioni, contributi e servizi alle imprese agricole', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 1, 1, 'Investimenti fissi e strutturali', 'Si classificano in questa posizione i documenti relativi ai contributi destinati a
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 1, 2, 'Accesso al capitale e al credito agrario', 'Si classificano in questa posizione i documenti relativi a interventi di facilitazione dell’accesso al credito e ad agevolazioni fiscali (finanziamento degli enti di garanzia operanti nel settore ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 1, 3, 'Sostegno alle forme associative e consortili', 'Si classificano in questa posizione i documenti relativi ad interventi volti a favorire lo sviluppo di forme associative di piccole imprese.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 1, 4, 'Servizi alle imprese', 'Si classificano in questa posizione i documenti relativi ad interventi volti a favorire la domanda e l’offerta di servizi alle imprese agricole, nonché quelli relativi al sostegno di attività di ricerca. Si classificano inoltre in questa posizione i documenti relativi alla rete dei centri di assistenza agricola sul territorio.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 1, 5, 'Imprenditoria giovanile', 'Si classificano in questa posizione i documenti relativi agli interventi diretti al sostegno dell’imprenditoria giovanile, quali contributi a favore del ricambio generazionale nell’ambito dell’attività agricola imprenditoriale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 1, 6, 'Danni e indennizzi', 'Si classificano in questa posizione i documenti relativi a contributi per danni, a prescindere dagli eventi che li hanno provocati (calamità naturali, selvaggina, fitopatìe ecc.). Si classificano in questa posizione i documenti relativi ai contributi per la realizzazione di sistemi di difesa attiva e passiva da tali eventi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 1, 7, 'Carburante agricolo agevolato', 'Si classificano in questa posizione i documenti relativi alle agevolazioni inerenti ai carburanti agricoli, ivi compresi i controlli a campione.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 2, 0, 'Infrastrutture agricole', 'Si classificano in questa posizione i documenti relativi agli interventi, effettuati o sostenuti dalla Provincia, relativi a opere collettive di miglioramento fondiario, irrigazione, bonifiche o interventi volti allo sviluppo delle infrastrutture rurali (sistemazione delle strade interpoderali, di acquedotti potabili, elettrificazione rurale, linee telefoniche, collegamenti fognari, nonché attrezzature necessarie alla loro manutenzione).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 3, 0, 'Consorzi di miglioramento fondiario e di bonifica', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza svolta dalla Provincia nei confronti dei consorzi di miglioramento fondiario e dei consorzi di bonifica. Si classificano in questa posizione, altresì, i documenti relativi all’erogazione di contributi per la costituzione e l’avviamento dei consorzi.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 4, 0, 'Organizzazione fondiaria', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 4, 1, 'Piani di riordino fondiario', 'Si classificano in questa posizione i documenti relativi ai piani di riordino fondiario e
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 4, 2, 'Proprietà diretto coltivatrice', 'Si classificano in questa posizione i documenti relativi ai contributi volti a promuovere il mantenimento dell’omogeneità del reticolo fondiario, quali quelli per l’acquisto di fondi rustici o terreni per la formazione di proprietà economicamente autosufficienti. Si classificano in questa posizione i documenti relativi alla verifica della sussistenza dei requisiti richiesti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 4, 3, 'Contratti agrari', 'Si classificano in questa posizione i documenti relativi all’attività di regolamentazione delle prelazioni e dei contratti di affitto agrario, ivi compresa la relativa attività di conciliazione.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 5, 0, 'Anagrafe delle imprese agricole', 'Si classificano in questa posizione i documenti relativi alla gestione dell’Anagrafe delle imprese agricole.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 6, 0, 'Archivio provinciale imprese agricole', 'Si classificano in questa posizione i documenti relativi alla gestione dell’archivio delle imprese agricole (APIA) (autorizzazione allo svolgimento dell’attività, inserimento e modifica dei dati relativi all’attività delle imprese, vigilanza sul loro operato, cancellazione, ecc.). Si classificano in questa posizione i documenti relativi ai ricorsi per negata iscrizione.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 7, 0, 'Associazionismo dei produttori agricoli', 'Si classificano in questa posizione i documenti relativi al riconoscimento delle associazioni operanti nel settore agricolo, nonché all’erogazione di contributi concessi loro o ai consorzi di tutela, quali quelli per le spese di avviamento.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 8, 0, 'Operatori del settore agricolo', 'Si classificano in questa posizione i documenti relativi all’organizzazione, al sostegno e alla gestione delle attività formative e di aggiornamento degli operatori del settore agricolo da parte della Provincia. Si classificano in questa posizione i documenti relativi sia alle attestazioni e certificazioni in materia di profili professionali degli operatori del settore (distillatori, organismi di certificazione delle produzioni biologiche, operatori ortofrutticoli ecc.,) per quanto di competenza provinciale, sia agli interventi di sostegno rivolti agli operatori del settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 9, 0, 'Produzioni agricole vegetali 10.9.1 Vitivinicoltura', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa in materia di impianti e reimpianti, all’attività di vigilanza e controllo del settore e alla gestione degli albi dei vigneti. Si classificano, altresì, in questa posizione i documenti relativi alla concessione di contributi per il rinnovo e/o la ristrutturazione di vigneti e in generale a quanto disciplinato dall’OCM vino.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 9, 2, 'Ortiflorofrutticoltura', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, all’attività di controllo della qualità dei prodotti ortofrutticoli, degli interventi di mercato, nonché ai contributi in favore del settore.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 9, 3, 'Olivicoltura', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, all’attività di controllo in materia di olivocoltura, nonché ai contributi in favore del settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 9, 4, 'Seminativi', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, all’attività di controllo in materia di seminativi, nonché ai contributi in favore del settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 10, 0, 'Qualità delle produzioni agroalimentari', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 10, 1, 'Produzioni integrate', 'Si classificano in questa posizione i documenti relativi all’attività di controllo e
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 10, 2, 'Agricoltura biologica', 'Si classificano in questa posizione i documenti relativi all’attività di controllo e regolamentazione svolta dalla Provincia in materia di metodi di produzione biologica. Si classificano, altresì, in questa posizione i documenti relativi ai contributi erogati a favore del settore.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 10, 3, 'Marchi delle produzioni agricole', 'Si classificano in questa posizione i documenti relativi alle attività di sostegno per il riconoscimento dei marchi delle produzioni agricole (IGP, IGT, DOP, DOC ecc.), e per la valorizzazione delle produzioni di qualità.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 10, 4, 'Prodotti tradizionali e valorizzazione delle produzioni', 'Si classificano in questa posizione i documenti relativi all’attività di tutela e promozione dei prodotti tradizionali trentini, nonché al riconoscimento e alla vigilanza sulle strade del vino e dei sapori. Si classificano inoltre in questa posizione i documenti relativi all’attività di vigilanza su produzione, etichettatura, rintracciabilità delle produzioni agroalimentari nonché sull’utilizzo di organismi geneticamente modificati');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 11, 0, 'Sorveglianza fitosanitaria 10.11.1 Attività vivaistica', 'Si classificano in questa posizione i documenti relativi ad autorizzazioni in materia di esercizio dell’attività vivaistica e al controllo dell’importazione e dell’esportazione di prodotti agricoli, compresa la tenuta del registro degli importatori di prodotti vegetali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 11, 2, 'Prodotti fitosanitari', 'Si classificano in questa posizione i documenti relativi all’attività di autorizzazione e vigilanza svolta in merito all’uso e alla vendita dei prodotti fitosanitari. Si classificano in questa posizione anche i documenti relativi ai corsi per le abilitazioni all’uso dei prodotti fitosanitari.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 11, 3, 'Organismi nocivi e da quarantena', 'Si classificano in questa posizione i documenti relativi all’attività di controllo svolta in materia di fitopatìe.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 12, 0, 'Patrimonio zootecnico', 'Si classificano in questa posizione i documenti connessi alle attività di salvaguardia e tutela del patrimonio zootecnico. Si classificano in particolare in questa classe i documenti relativi
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 13, 0, 'Allevamenti', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 13, 1, 'Allevamenti bovini', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, al controllo e ai contributi connessi alla gestione del settore, ivi compresi quelli per gli abbattimenti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 13, 2, 'Allevamenti equini', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, al controllo e ai contributi connessi alla gestione del settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 13, 3, 'Allevamenti ovicaprini', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, al controllo e ai contributi connessi alla gestione del settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 13, 4, 'Allevamenti suini', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, al controllo e ai contributi connessi alla gestione del settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 13, 5, 'Acquacoltura', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, al controllo e ai contributi connessi alla gestione del settore, quali ad esempio i programmi comunitari FEP.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 13, 6, 'Apicoltura', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, al controllo e ai contributi connessi alla gestione del settore');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 13, 7, 'Allevamenti minori', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, al controllo e ai contributi connessi alla gestione delle tipologie di allevamento non ricompresi nelle sottoclassi precedenti (elicicoltura, lombricoltura ecc.)');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 14, 0, 'Produzioni zootecniche 10.14.1 Carni', 'Si classificano in questa posizione i documenti relativi controllo e ai contributi in materia di produzione di carne animale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 14, 2, 'Produzioni lattiero-casearie', 'Si classificano in questa posizione i documenti relativi
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 14, 3, 'Produzioni minori', 'Si classificano in questa posizione i documenti relativi alla regolamentazione, al controllo e ai contributi in materia di produzioni agricole animali minori.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 15, 0, 'Agriturismo', 'Si classificano in questa posizione i documenti relativi all’autorizzazione all’esercizio di attività agrituristiche e all’attività di vigilanza sulle stesse, nonché ai contributi ad esse concessi. Si classificano, altresì, in questa posizione i documenti relativi all’Albo agritur.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (10, 16, 0, 'Agroambiente', 'Si classificano in questa posizione i documenti relativi alla tutela e alla salvaguardia del sistema agro-silvo-pastorale, mediante ad esempio l’incentivazione di tecniche agricole rispettose dell’ambiente, di conservazione di coltivazioni caratteristiche o di salvaguardia delle razze animali da allevamento autoctone.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 1, 0, 'Agevolazioni, contributi e servizi agli enti e alle imprese agro-silvo-forestali 11.1.1 Fondo forestale', 'Si classificano in questa posizione i documenti relativi alla gestione del fondo forestale provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 1, 2, 'Interventi forestali', 'Si classificano in questa posizione i documenti relativi ai contributi erogati a enti o privati per interventi realizzati sul patrimonio forestale, quali rimboschimenti, interventi colturali o per l’acquisto di attrezzature ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 1, 3, 'Filiera foresta-legno', 'Si classificano in questa posizione i documenti relativi agli interventi volti al sostegno della produzione di legnami.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 2, 0, 'Gestione delle foreste demaniali', 'Si classificano in questa posizione i documenti relativi alla gestione del demanio forestale provinciale (concessioni, autorizzazioni ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 3, 0, 'Utilizzazioni boschive', 'Si classificano in questa posizione i documenti relativi alle utilizzazioni boschive, ivi compresi i capitolati d’onere, generali e particolari.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 4, 0, 'Interventi e opere forestali', 'Si classificano in questa posizione i documenti relativi agli interventi in materia forestale quali rinsaldamento di terreni franosi, operazioni di sfollamento e diradamento o rimboschimento. Si classificano, inoltre, in questa posizione i documenti relativi alla realizzazione di infrastrutture forestali, quali poste di esbosco, piazzali di prime lavorazioni e deposito legname, rifugi per gli operai, strade forestali e opere antincendio. Si classificano in questa posizione anche i documenti relativi all’affidamento a terzi di tali lavori.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 5, 0, 'Piante, vivai e semi forestali', 'Si classificano in questa posizione i documenti relativi alla gestione dei vivai forestali (movimento piantine ecc.) e alle cessioni di piante ornamentali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 6, 0, 'Monitoraggio del patrimonio forestale e montano', 'Si classificano in questa posizione i documenti relativi all’attività di monitoraggio e diagnosi di danni boschivi e ambientali derivanti da fenomeni quali incendi, fitopatologie, attacchi parassitari, o da agenti inquinanti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 7, 0, 'Operatori del settore forestale', 'Si classificano in questa posizione i documenti relativi all’organizzazione, al sostegno e alla gestione delle attività formative e di aggiornamento degli operatori del settore forestale (corsi per le utilizzazioni boschive, per boscaioli ecc.). Si classificano, altresì, in questa posizione i documenti relativi alle certificazioni in materia di idoneità tecnica al lavoro in bosco.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 8, 0, 'Tutela della flora e di altri prodotti del bosco', 'Si classificano in questa posizione i documenti relativi all’attività di tutela e salvaguardia della flora alpina, ivi comprese le autorizzazioni alla raccolta. Si classificano, altresì, in questa
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 9, 0, 'Tutela della fauna', 'Si classificano in questa posizione i documenti relativi alle attività in materia di protezione e salvaguardia della fauna selvatica, anche minore, ivi compresa l’attività autorizzativa e gli interventi effettuati in materia di popolamento (immissioni di specie autoctone e non autoctone, abbattimenti o catture) e prelevamento (detenzioni ecc.). Si classificano altresì in questa posizione i documenti relativi all’attività di studio e monitoraggio delle risorse faunistiche, quali censimenti, avvistamenti casuali ecc. Si classificano in questa posizione, inoltre, i documenti relativi alle attività di monitoraggio e protezione della fauna ittica, ivi compresa l’autorizzazione all’immissione di specie ittiche estranee alla fauna locale, alle semine ittiche, al ricorso a strumenti speciali per la cattura ecc. Si classificano in questa posizione i documenti relativi alla predisposizione della carta ittica, nonché quelli relativi a segnalazioni e interventi per malattie, morìe di pesci ecc.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 10, 0, 'Caccia', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 10, 1, 'Riserve di caccia', 'Si classificano in questa posizione i documenti relativi all’attività di programmazione, definizione e gestione delle riserve di caccia presenti sul territorio provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 10, 2, 'Ente gestore e associazionismo', 'Si classificano in questa posizione i documenti relativi ai contributi erogati a favore dell’ente gestore e più in generale all’attività di convenzionamento ad esso connessa.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 10, 3, 'Esercizio della caccia', 'Si classificano in questa posizione i documenti relativi alle attività connesse all’esercizio della caccia, ivi comprese le abilitazioni e la gestione dei relativi esami, i riconoscimenti delle qualifiche venatorie, le attività autorizzative in materia di predisposizione delle postazioni di caccia ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 11, 0, 'Pesca', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 11, 1, 'Diritti di pesca', 'Si classificano in questa posizione i documenti relativi all’attività di concessione dei diritti di pesca.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 11, 2, 'Associazioni e società concessionarie', 'Si classificano in questa posizione i documenti relativi ai contributi erogati a favore delle società e/o associazioni concessionarie dei diritti di pesca e più in generale alla connessa attività di convenzionamento. Si classificano in questa posizione i documenti relativi all’attività di monitoraggio su regolamenti e attività.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 11, 3, 'Esercizio della pesca', 'Si classificano in questa posizione i documenti relativi all’esercizio della pesca, ivi compresi quelli inerenti alle relative procedure di abilitazione. Si classificano, altresì, in questa posizione i documenti relativi al rilascio delle licenze di pesca.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 12, 0, 'Consorzi di vigilanza boschiva', 'Si classificano in questa posizione i documenti relativi alle attività dei consorzi di vigilanza boschiva, quali ad esempio le giurisdizioni e le nomine dei custodi forestali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 13, 0, 'Guardie giurate', 'Si classificano in questa posizione i documenti relativi alla nomina o al rinnovo delle attribuzioni di funzioni di guardia per vigilanza in materia faunistico-venatoria, ittica, ecologica, zoofila ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 14, 0, 'Corpo forestale', 'Si classificano in questa posizione i documenti relativi all’organizzazione dell’attività del Corpo forestale della Provincia Autonoma di Trento e alla gestione degli affari generali inerenti al suo personale.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (11, 15, 0, 'Polizia forestale e ambientale', 'Si classificano in questa posizione i documenti relativi alle attività in materia di polizia e vigilanza forestale, ivi compresi quelli in materia idraulica, venatoria, faunistica, ittica ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (12, 1, 0, 'Agevolazioni, contributi e servizi alle imprese industriali', 'Si classificano in questa posizione i documenti relativi alla concessione di agevolazioni e contributi nei confronti di imprese industriali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (12, 2, 0, 'Promozione e sviluppo delle attività industriali ed economiche', 'Si classificano in questa posizione i documenti relativi alla promozione e al sostegno dello sviluppo delle attività industriali ed economiche.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (12, 3, 0, 'Aree per insediamenti produttivi', 'Si classificano in questa posizione i documenti relativi alla progettazione e alla realizzazione di aree destinate ad insediamenti produttivi, unitamente a quelli relativi all’attività rivolta allo sviluppo e al miglioramento delle infrastrutture. Si classificano in questa posizione anche i documenti relativi alle procedure di assegnazione, cessione o vendita delle aree industriali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (12, 4, 0, 'Promozione e sviluppo delle attività estrattive', 'Si classificano in questa posizione i documenti relativi ai contributi diretti alla promozione e al sostegno delle attività estrattive con particolare riferimento alla coltivazione delle cave.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (12, 5, 0, 'Giacimenti minerari e cave', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (12, 5, 1, 'Ricerca e coltivazione di giacimenti minerari', 'Si classificano in questa posizione i documenti relativi all’esercizio delle pratiche di
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (12, 5, 2, 'Coltivazione delle cave', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa in materia di cave per quanto di competenza provinciale, ivi compresa quella in materia di pianificazione (piano cave, aggiornamenti, variazioni ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (12, 6, 0, 'Polizia mineraria', 'Si classificano in questa posizione i documenti relativi all’attività di sorveglianza e sanzionatoria in materia di polizia mineraria. Si classificano in particolare in questa posizione i documenti relativi alla verifica della sicurezza, alla segnalazione di infortuni, alla tutela della salute dei lavoratori in miniere e cave, nonché agli adempimenti connessi all’utilizzo degli esplosivi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (13, 1, 0, 'Agevolazioni, contributi e servizi alle imprese artigiane', 'Si classificano in questa posizione i documenti relativi alla concessione di agevolazioni e contributi nei confronti di imprese artigiane.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (13, 2, 0, 'Promozione e valorizzazione delle attività artigianali', 'Si classificano in questa posizione i documenti relativi ai contributi diretti alla promozione e al sostegno delle attività artigianali. In particolare si classificano in questa posizione i documenti relativi a contributi per progetti di valorizzazione dell’impresa artigiana a sostegno dei mestieri e dei prodotti artigianali tipici, per le botteghe-scuola istituite per la trasmissione del mestiere dell’artigiano ai giovani, nonché per quelli concernenti il riconoscimento delle imprese d’eccellenza.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (13, 3, 0, 'Professioni artigiane', 'Si classificano in questa posizione i documenti relativi all’accertamento dei requisiti di qualificazione professionale (parrucchieri, estetisti ecc.) e alla verifica della sussistenza dei presupposti e dei requisiti di legge per l’esercizio di attività artigiane (estetisti, imprese di pulizie o facchinaggio ecc.). Si classificano in questa posizione anche i documenti relativi agli orari di apertura delle attività artigiane, ai pareri vincolanti in merito ai regolamenti comunali che disciplinano specifiche attività artigianali e più in generale all’attività consultiva e di controllo svolta dalla Provincia nel settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (13, 4, 0, 'Albo delle imprese artigiane', 'Si classificano in questa posizione i documenti relativi alla gestione dell’Albo per quanto di competenza provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (14, 1, 0, 'Agevolazioni, contributi e servizi alle imprese commerciali', 'La classe comprende il carteggio relativo alla concessione di agevolazioni e contributi nei confronti di imprese commerciali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (14, 2, 0, 'Promozione e sviluppo delle attività commerciali', 'Rientrano nella classe i carteggi inerenti ai contributi diretti alla promozione e al sostegno delle attività commerciali. La classe comprende i documenti relativi al sostegno ai Centri di assistenza tecnica (CAT), alla promozione dei luoghi storici del commercio, alle botteghe storiche ed ai mercati agricoli di “filiera corta”, nonché la documentazione relativa a interventi volti al sostegno della distribuzione e rilocalizzazione delle imprese commerciali sul territorio provinciale, con specifico riferimento alle aree marginali o svantaggiate.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (14, 3, 0, 'Commercio fisso e ambulante', 'Afferiscono alla classe i documenti relativi all’attività autorizzativa e di vigilanza nei confronti del commercio fisso e ambulante. Sono altresì ricompresi in quest’ambito i documenti relativi alla regolamentazione e al monitoraggio del sistema di distribuzione provinciale (orari degli esercizi nei comuni ad economia turistica e terziaria, definizione di regole e periodi per saldi di fine stagione, promozioni, politiche dei prezzi ecc.), nonché quelli inerenti alle autorizzazioni all’esercizio dell’attività da parte di grandi strutture di vendita. NOTA bene – I pareri forniti relativamente agli aspetti di pianificazione commerciale negli strumenti urbanistici locali rientrano nella sottoclasse 18.2.2; i documenti relativi ai contributi destinati alla Camera di commercio per l’esercizio di funzioni delegate vanno ricondotti alla classe 5.7.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (14, 4, 0, 'Impianti di distribuzione di carburante', 'Rientrano nella classe i documenti relativi alle attività di competenza provinciale inerenti agli impianti stradali o di uso privato di distribuzione di carburante.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (14, 5, 0, 'Manifestazioni e quartieri fieristici', 'La classe comprende la documentazione relativa alle attività in materia di fiere, ai contributi per le manifestazioni fieristiche, nonché agli incentivi e servizi per la realizzazione e la gestione dei quartieri fieristici da parte di soggetti terzi, comprese le partecipazioni azionarie nelle società proprietarie dei quartieri fieristici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (14, 6, 0, 'Tutela dei consumatori', 'Rientrano in questa classe i documenti concernenti la tutela dei diritti dei consumatori e degli utenti, nonché gli incentivi e i servizi a loro favore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 1, 0, 'Agevolazioni, contributi e servizi alle imprese turistiche', 'Si classificano in questa posizione i documenti relativi alla richiesta e alla successiva concessione di agevolazioni e contributi da parte della Provincia nei confronti di imprese turistiche, ivi comprese le attività idrotermali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 2, 0, 'Organizzazione turistica del territorio', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 2, 1, 'Società di promozione turistica e territoriale', 'Si classificano in questa posizione i documenti relativi all’attività della società di
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 2, 2, 'Associazioni e consorzi di associazioni pro loco', 'Si classificano in questa posizione i documenti relativi alle attività di organizzazione del sistema turistico locale tramite il riconoscimento giuridico e il sostegno alle associazioni pro loco e a loro consorzi. Si classificano in questa posizione i documenti relativi alla gestione dell’elenco delle associazioni pro loco.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 2, 3, 'Aziende per il turismo', 'Si classificano in questa posizione i documenti relativi all’attività di riconoscimento giuridico, al sostegno e al coordinamento delle Aziende per il turismo (APT).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 3, 0, 'Promozione territoriale e delle produzioni trentine', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 3, 1, 'Manifestazioni e iniziative turistiche', 'Si classificano in questa posizione i documenti relativi alle attività volte al sostegno di
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 3, 2, 'Commercializzazione dei prodotti turistici trentini', 'Si classificano in questa posizione i documenti relativi all’attività di autorizzazione alla vendita e all’intermediazione di pacchetti turistici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 3, 3, 'Commercializzazione dei prodotti trentini', 'Si classificano in questa posizione i documenti relativi alle attività volte al sostegno della promozione e della commercializzazione di prodotti trentini sia in ambito nazionale che extranazionale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 3, 4, 'Marchio territoriale e promozione diretta dei prodotti trentini', 'Si classificano in questa posizione i documenti relativi alla gestione dei marchi territoriali e per le produzioni tipiche, oltre a quelli relativi alle attività di promozione diretta dei prodotti trentini.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 4, 0, 'Professioni turistiche', 'Si classificano in questa posizione i documenti relativi alle competenze provinciali in materia di professioni turistiche, ivi compresi in particolare quelli relativi alle abilitazioni al loro esercizio e alla connessa attività di vigilanza. Si classificano altresì in questa posizione i documenti relativi ai contributi destinati a scuole di sci e alpinismo, ai collegi provinciali delle professioni turistiche ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 5, 0, 'Agenzie di viaggio', 'Si classificano in questa posizione i documenti relativi alle autorizzazioni all’esercizio di agenzie di viaggio, ivi compresi gli atti di variazione di titolarità e di cessazione dell’attività
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 6, 0, 'Strutture ricettive alberghiere', 'Si classificano in questa posizione i documenti relativi alle competenze provinciali in materia di strutture ricettive alberghiere. Si classificano in particolare in questa posizione i documenti relativi alla determinazione della classificazione alberghiera e alla verifica della sussistenza dei requisiti, alle tabelle dei prezzi alberghieri e alla concessione dei visti di corrispondenza per i medesimi esercizi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 7, 0, 'Strutture ricettive extralberghiere', 'Si classificano in questa posizione i documenti relativi alle competenze provinciali in materia di strutture ricettive extralberghiere (bed & breakfast, affittacamere, case e appartamenti per vacanze, strutture per turismo sociale, case per ferie ecc.). Si classificano in particolare in questa posizione i documenti relativi all’attività di vigilanza sulla persistenza dei requisiti e sui prezzi, nonché i documenti relativi ai contributi concessi ai comuni per la costruzione, l’acquisto e la ristrutturazione di immobili da adibire ad ostelli della gioventù.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 8, 0, 'Campeggi', 'Si classificano in questa posizione i documenti relativi alle competenze provinciali in materia di strutture ricettive all’aperto. Si classificano in particolare in questa posizione i documenti relativi all’attività di vigilanza sulla persistenza dei requisiti e sui prezzi, nonché quelli relativi ai contributi concessi per l’esercizio dell’attività dei campeggi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 9, 0, 'Strutture alpinistiche', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 9, 1, 'Rifugi alpini, escursionistici e bivacchi', 'Si classificano in questa posizione i documenti relativi alle attività autorizzative in
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 9, 2, 'Tracciati alpini', 'Si classificano in questa posizione i documenti relativi alle autorizzazioni in materia di sentieri alpini, sentieri alpini attrezzati, vie ferrate e vie alpinistiche, nonché alla relativa attività di vigilanza.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 10, 0, 'Piste da sci', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e di vigilanza in materia di apprestamento di piste da sci e di strutture connesse al loro esercizio (impianti d’innevamento ecc.). Si classificano inoltre in questa posizione i documenti relativi ai contributi erogati per l’esercizio di piste da sci, nonché per l’organizzazione di corsi di formazione per gli operatori del settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 11, 0, 'Linee funiviarie e impianti a fune in servizio pubblico', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 11, 1, 'Esercizio delle linee funiviarie e degli impianti', 'Si classificano in questa posizione i documenti relativi all’attività concessoria in
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 11, 2, 'Vigilanza e sorveglianza tecnico-amministrativa sugli impianti', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza e sorveglianza tecnico-amministrativa esercitata dalla Provincia nei confronti degli impianti in esercizio, ivi comprese le revisioni, le verifiche e le ispezioni, a prescindere dalla loro periodicità. Si classificano inoltre in questa posizione i documenti relativi all’attività sanzionatoria connessa.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 11, 3, 'Collaudi e prove tecniche sugli impianti', 'Si classificano in questa posizione i documenti relativi alle attività di collaudo e verifica tecnica svolte tanto nell’ambito dell’attività istituzionale della Provincia quanto per conto terzi. Si classificano, altresì, in questa posizione i documenti relativi alla determinazione degli oneri di collaudo.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (15, 11, 4, 'Abilitazioni e patenti funiviarie', 'Si classificano in questa posizione i documenti relativi al rilascio delle patenti di abilitazione degli operatori degli impianti a fune, ivi compresi quelli relativi agli esami, nonché alla tenuta del registro dei tecnici responsabili.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (16, 1, 0, 'Agevolazioni, contributi e servizi alle cooperative', 'Si classificano in questa posizione i documenti relativi alla concessione di agevolazioni e contributi nei confronti delle cooperative.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (16, 2, 0, 'Promozione e sviluppo della cooperazione', 'Si classificano in questa posizione i documenti relativi ad interventi volti al sostegno della diffusione e dello sviluppo del fenomeno cooperativo.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (16, 3, 0, 'Vigilanza sugli enti cooperativi', 'Si classificano in questa posizione i documenti relativi alle pratiche di iscrizione e/o cancellazione delle cooperative e dei consorzi di cooperative al competente Registro; si classificano, inoltre, in questa posizione i documenti relativi all’attività di revisione sugli enti cooperativi. Si classificano in questa posizione anche i documenti relativi alla gestione delle procedure fallimentari e di liquidazione delle cooperative e di gestione del fondo mutualistico provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 1, 0, 'Controlli e indagini ambientali', 'Si classificano in questa posizione i documenti relativi alle attività di controllo in materia di inquinamento, nonché alle indagini finalizzate alla conoscenza dello stato di salute dell’ambiente e delle cause di inquinamento.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 2, 0, 'Attività di laboratorio', 'Si classificano in questa posizione i documenti relativi alle attività di laboratorio svolte in materia di prevenzione di ogni forma di inquinamento.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 3, 0, 'Monitoraggio ambientale', 'Si classificano in questa posizione i documenti relativi alla raccolta e alla diffusione dei dati inerenti all’attività di monitoraggio ambientale, condotta attraverso le reti di rilevamento della qualità di acqua e aria, nonché attraverso le reti di rilevamento di inquinamento acustico e campi elettromagnetici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 4, 0, 'Gestione ambientale', 'Si classificano in questa posizione i documenti relativi al rilascio di autorizzazioni e permessi in materia ambientale, nonché i documenti relativi alla connessa attività di vigilanza.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 5, 0, 'Tutela, risanamento e bonifica ambientale', 'Si classificano in questa posizione i documenti relativi alle attività volte ad assicurare la tutela e il risanamento delle acque e dell’aria, nonché la bonifica dei siti inquinati e la tutela dagli agenti fisici (rumore e campi elettromagnetici). Si classificano in questa posizione i documenti relativi alla redazione dei piani di tutela e risanamento delle acque e dell’aria, nonché alla pianificazione degli agenti fisici. Si classificano, inoltre, in questa posizione i documenti relativi alla pianificazione in materia di gestione dei rifiuti e, in particolare, alla predisposizione e aggiornamento del piano provinciale di smaltimento');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 6, 0, 'Valutazione ambientale', 'Si classificano in questa posizione i documenti relativi all’attività di valutazione ambientale e alle procedure ad essa connessa (VIA, screening, delimitazione del campo di indagine, RIA, VAS ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 7, 0, 'Valorizzazione e promozione dell’ambiente e delle risorse naturali', 'Si classificano in questa posizione i documenti relativi alle attività di valorizzazione e di promozione della conoscenza dell’ambiente e delle risorse naturali e all’attività di educazione ambientale per le scuole.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 8, 0, 'Gestione dei rifiuti', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 8, 1, 'Impianti di smaltimento, discariche e centri di rottamazione', 'Si classificano in questa posizione i documenti relativi alla progettazione,
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 8, 2, 'Raccolta, trasporto e smaltimento dei rifiuti', 'Si classificano in questa posizione i documenti relativi alle varie fasi di gestione dei rifiuti (smaltimento, riuso, riciclaggio, raccolta differenziata ecc.). Si classificano, inoltre, in questa posizione i documenti relativi al trasporto e alla rimozione dei rifiuti, nonché quelli relativi alla gestione del servizio di raccolta e trattamento dei rifiuti di ogni tipo, ivi compresa l’organizzazione dei centri di compostaggio, di raccolta o delle stazioni di trasferimento. Si classificano in questa posizione i documenti relativi alle operazioni connesse alla gestione dei rifiuti speciali e pericolosi.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 9, 0, 'Opere di ripristino ambientale', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 9, 1, 'Pianificazione delle opere di ripristino', 'Si classificano in questa posizione i documenti relativi alla pianificazione delle opere
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 9, 2, 'Progettazione, realizzazione e manutenzione', 'Si classificano in questa posizione i documenti relativi alla progettazione, realizzazione e manutenzione delle opere di ripristino ambientale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 10, 0, 'Interventi per lo sviluppo sostenibile e l’ambiente', 'Si classificano in questa posizione i documenti relativi all’attività di finanziamento e sostegno degli interventi in materia di ecologia, ambiente e cambiamento climatico. Si classificano in questa posizione, inoltre, i documenti relativi all’attività di supporto alle certificazioni ambientali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 11, 0, 'Aree protette 17.11.1 Parchi', 'Si classificano in questa posizione i documenti relativi alla prevenzione, al controllo e alla tutela dei parchi. Si classificano in questa posizione anche i documenti relativi all’attività autorizzativa, contributiva e di promozione e sensibilizzazione di settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 11, 2, 'Riserve naturali e parchi fluviali', 'Si classificano in questa posizione i documenti relativi alla prevenzione, al controllo e alla tutela delle riserve naturali e dei parchi fluviali. Si classificano in questa posizione anche i documenti relativi all’attività autorizzativa, contributiva e di promozione e sensibilizzazione di settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 11, 3, 'Aree destinate alla conservazione della diversità biologica', 'Si classificano in questa posizione i documenti relativi alla prevenzione, al controllo e alla tutela delle aree destinate alla conservazione della diversità biologica. Si classificano in questa posizione anche i documenti relativi all’attività autorizzativa, contributiva e di promozione e sensibilizzazione di settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 12, 0, 'Tutela del patrimonio speleologico, mineralogico, paleontologico e fossile', 'Si classificano in questa posizione i documenti relativi alle attività condotte a tutela e valorizzazione del patrimonio speleologico, mineralogico, paleontologico e fossile, ivi comprese le connesse procedure autorizzative e concessorie. Si classificano in questa posizione i documenti relativi alla gestione del catasto grotte e aree carsiche.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 13, 0, 'Energia', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 13, 1, 'Pianificazione energetica', 'Si classificano in questa posizione i documenti relativi alle attività di pianificazione in
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 13, 2, 'Gestione delle fonti e delle reti di distribuzione energetica', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e concessoria in materia di fonti energetiche e di distribuzione delle medesime. In particolare, si classificano in questa posizione i documenti relativi alla realizzazione e modifica di elettrodotti, all’installazione di impianti di generazione da fonti rinnovabili e convenzionali, nonché all’installazione, modifica e dismissione di serbatoi per oli minerali. Si classificano inoltre in questa posizione i documenti relativi all’installazione di sonde geotermiche e alla connessa attività di vigilanza.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 13, 3, 'Promozione e incentivi nel settore energetico', 'Si classificano in questa posizione i documenti relativi alla promozione e agli incentivi in materia di risparmio energetico, produzione di energia da fonti rinnovabili e più in generale destinati al settore di produzione e distribuzione energetica. Si classificano in questa posizione i documenti relativi alle certificazioni in materia di prestazione energetica degli edifici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (17, 13, 4, 'Vigilanza e controllo sull’esercizio degli impianti termici', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza e controllo sulla manutenzione e l’esercizio degli impianti termici, nonché alla relativa attività sanzionatoria. Si classificano in questa posizione anche i documenti relativi alla gestione del catasto degli impianti termici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 1, 0, 'Cartografia e geodesia', 'Si classificano in questa posizione i documenti relativi alle procedure per la determinazione della base cartografica, l’elaborazione, la produzione e la distribuzione della cartografia provinciale. Si classificano in particolare in questa posizione i documenti relativi gli adempimenti connessi alla carta tecnica provinciale, alla cartografia geologica e alle altre cartografie tematiche.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 2, 0, 'Pianificazione urbanistica', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 2, 1, 'Piano urbanistico provinciale', 'Si classificano in questa posizione i documenti relativi alla formazione del piano
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 2, 2, 'Strumenti di pianificazione urbanistica locale', 'Si classificano in questa posizione i documenti relativi alla gestione dei procedimenti istruttori ed al coordinamento delle attività di valutazione degli strumenti delle comunità e dei comuni. Si classificano inoltre in questa posizione i documenti relativi all’attività di supporto tecnico e finanziario svolta a beneficio degli enti locali in fase di redazione dei rispettivi strumenti urbanistici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 2, 3, 'Accertamenti di conformità urbanistica', 'Si classificano in questa posizione i documenti relativi all’attività di accertamento di conformità delle opere pubbliche di interesse di amministrazioni terze rispetto alla Provincia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 2, 4, 'Deroghe urbanistiche', 'Si classificano in questa posizione i documenti relativi ai nulla osta rilasciati per le concessioni in deroga.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 2, 5, 'Vigilanza e controllo sull’attività urbanistica ed edilizia', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza e controllo sugli atti comunali concernenti la repressione degli abusi edilizi e sulla legittimità degli atti degli enti locali in materia di urbanistica ed edilizia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 3, 0, 'Tutela paesaggistico-ambientale e degli insediamenti storici', 'Si classificano in questa posizione i documenti relativi all’attività di pianificazione, individuazione e recupero del patrimonio soggetto a tutela paesaggistico-ambientale, ivi comprese le attività autorizzative e di supporto in materia. Si classificano in questa posizione i documenti relativi alla tenuta dell’albo degli esperti di urbanistica e tutela del paesaggio. Si classificano inoltre in questa posizione i documenti relativi all’attività di pianificazione, individuazione e recupero degli insediamenti storici, nonché quelli inerenti ai contributi destinati a tale attività.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 4, 0, 'Interventi di sistemazione dei corsi d’acqua e dei bacini idrografici', 'Si classificano in questa posizione i documenti relativi alla realizzazione diretta o da parte di terzi di interventi di sistemazione e difesa dell’assetto idraulico e idrogeologico del territorio, quali opere di consolidamento di alvei dei corsi d’acqua, sistemazione di frane e versanti instabili, manutenzione delle opere di sistemazione, della vegetazione e degli alvei.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 5, 0, 'Gestione del demanio idrico', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e concessoria in materia di interventi da parte di terzi sul demanio idrico provinciale (corsi d’acqua, laghi, ghiacciai iscritti nell’elenco delle acque pubbliche, opere di difesa pubbliche).
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 6, 0, 'Utilizzazione delle risorse idriche', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 6, 1, 'Piano generale di utilizzazione delle acque pubbliche', 'Si classificano in questa posizione i documenti relativi alla pianificazione dell’utilizzo
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 6, 2, 'Gestione delle acque pubbliche', 'Si classificano in questa posizione i documenti relativi alle autorizzazioni, concessioni o altri provvedimenti in materia di utilizzazione delle acque pubbliche.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 6, 3, 'Servizio idrico integrato', 'Si classificano in questa posizione i documenti relativi alle attività connesse alla gestione, allo sviluppo e al monitoraggio dei servizi idrici di utilizzo o distribuzione.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 7, 0, 'Pianificazione forestale e montana', 'Si classificano in questa posizione i documenti relativi all’attività di pianificazione forestale e montana (piani forestali e montani, piani aziendali, piani degli interventi, piani per la difesa antincendi, piani delle aree protette).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (18, 8, 0, 'Vincolo idrogeologico e forestale', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza connessa alla salvaguardia dei terreni soggetti a vincolo idrogeologico. In particolare, si classificano in questa posizione i documenti relativi alle autorizzazioni in materia di realizzazione di cave e torbiere, impianti di trasporto a fune, piste da sci, trasformazioni colturali, edificazioni e autorizzazioni al pascolo. Si classificano altresì in questa posizione i documenti relativi alle autorizzazioni connesse alla gestione delle strade forestali, alla loro classificazione ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 1, 0, 'Edilizia pubblica', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 1, 1, 'Progettazione e realizzazione', 'Si classificano in questa posizione i documenti relativi alla progettazione e alla
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 1, 2, 'Manutenzione ordinaria e straordinaria, risanamento e restauro', 'Si classificano in questa posizione i documenti relativi all’effettuazione di lavori di manutenzione ordinaria e straordinaria, risanamento, ristrutturazione e restauro d’immobili nella disponibilità della Provincia, ivi compresa la relativa impiantistica. Si classificano in questa posizione anche i documenti relativi agli interventi condotti direttamente sugli immobili che la Provincia detenga o acquisisca a titolo di locazione o di comodato.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 2, 0, 'Vigilanza sulle opere in cemento armato e strutture metalliche', 'Si classificano in questa posizione i documenti relativi all’attività di controllo sulle opere in cemento armato e strutture metalliche.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 3, 0, 'Espropriazioni per pubblica utilità a vantaggio di terzi', 'Si classificano in questa posizione i documenti relativi all’attività svolta dalla Provincia in materia di espropriazione per pubblica utilità di beni destinati a non ricadere nel patrimonio provinciale.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 4, 0, 'Prove geotecniche e indagini geognostiche', 'Si classificano in questa posizione i documenti relativi alle prove geotecniche e alle indagini geognostiche svolte sia per conto provinciale sia per conto terzi dalle competenti strutture. Si classificano inoltre in questa posizione i documenti relativi allo studio e alla caratterizzazione dei materiali edili storici trentini.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 5, 0, 'Infrastrutture stradali e viabilità', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 5, 1, 'Progettazione e realizzazione', 'Si classificano in questa posizione i documenti relativi alla progettazione, alla
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 5, 2, 'Controlli e prove tecniche sui materiali', 'Si classificano in questa posizione i documenti relativi alle prove sui materiali attuate nel competente laboratorio provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 5, 3, 'Manutenzione ordinaria e straordinaria', 'Si classificano in questa posizione i documenti relativi alle attività di manutenzione ordinaria e straordinaria delle strade e delle relative strutture. Si classificano inoltre in questa
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 5, 4, 'Gestione della rete viaria', 'Si classificano in questa posizione i documenti relativi agli adempimenti connessi al rilascio di concessioni ed autorizzazioni per l’utilizzo della sede stradale e delle relative pertinenze, ivi comprese quelle rilasciate per l’effettuazione di trasporti eccezionali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 5, 5, 'Sicurezza stradale, controllo e vigilanza sulla rete viaria', 'Si classificano in questa posizione i documenti relativi alle attività svolte dalla Provincia in materia di sicurezza stradale nonché di salvaguardia del patrimonio stradale provinciale, ivi compresa la relativa attività sanzionatoria.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 5, 6, 'Circolazione stradale', 'Si classificano in questa posizione i documenti relativi alle attività di competenza provinciale in materia di circolazione stradale, ivi comprese la sua regolazione e le relative ordinanze. Si classificano, altresì, in questa posizione i documenti relativi alle attività di censimento e rilevazione della circolazione e del traffico.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 5, 7, 'Catasto strade', 'Si classificano in questa posizione i documenti relativi all’attività di tenuta e aggiornamento del catasto strade, ivi compresi quelli relativi alle procedure di classificazione o declassificazione delle strade.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 6, 0, 'Infrastrutture ferroviarie', 'Si classificano in questa posizione i documenti relativi alla progettazione, costruzione, realizzazione e manutenzione ordinaria e straordinaria delle reti ferroviarie e delle relative strutture, ivi comprese le opere di ampliamento o ristrutturazione.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 7, 0, 'Piste ciclopedonali', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 7, 1, 'Progettazione e realizzazione', 'Si classificano in questa posizione i documenti relativi alla pianificazione,
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 7, 2, 'Gestione e manutenzione', 'Si classificano in questa posizione i documenti relativi agli adempimenti connessi alla gestione della rete ciclopedonale e delle relative pertinenze (autorizzazioni al transito, ordinanze, manutenzione ordinaria e straordinaria ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 8, 0, 'Impianti a fune in servizio privato', 'Si classificano in questa posizione i documenti relativi a pareri e autorizzazioni in merito all’esercizio di impianti a fune adibiti al servizio privato.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 9, 0, 'Opere igienico-sanitarie', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 9, 1, 'Progettazione e realizzazione', 'Si classificano in questa posizione i documenti relativi alla progettazione, alla
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 9, 2, 'Gestione e manutenzione', 'Si classificano in questa posizione i documenti relativi alle attività di ordinaria e straordinaria manutenzione delle opere igienico-sanitarie e delle relative pertinenze. Si classificano inoltre in questa posizione i documenti relativi all’affidamento in gestione degli impianti e alle attività di verifica connesse, nonché all’attività di vigilanza tecnica sugli impianti (prove di impermeabilità dei collettori ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 9, 3, 'Catasto delle opere di depurazione', 'Si classificano in questa posizione i documenti relativi alla gestione del catasto delle opere di depurazione e dei collettori principali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (19, 10, 0, 'Prezziario', 'Si classificano in questa posizione i documenti relativi alla predisposizione, all’aggiornamento e alla pubblicazione dell’elenco prezzi.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 1, 0, 'Servizi pubblici di trasporto', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 1, 1, 'Trasporto ferroviario', 'Si classificano in questa posizione i documenti relativi alla pianificazione, alla
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 1, 2, 'Autolinee e autotrasporto pubblico', 'Si classificano in questa posizione i documenti relativi alla pianificazione, alla programmazione e alla gestione dei servizi di autotrasporto pubblico, ivi compreso l’affidamento dei servizi di trasporto pubblico provinciale. Si classificano in questa posizione i documenti relativi a finanziamento e sostegno degli investimenti nei servizi di trasporto pubblico, anche connessi alle forme di mobilità alternativa.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 1, 3, 'Trasporto scolastico', 'Si classificano in questa posizione i documenti relativi alla pianificazione, alla programmazione e alla gestione dei servizi di trasporto degli alunni frequentanti le scuole dell’infanzia e le scuole primarie di primo e secondo grado, compresi quelli predisposti per gli alunni portatori di handicap frequentanti asili nido, scuole dell’infanzia o istituti scolastici di ogni ordine e grado nonché centri socio-educativi e terapici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 1, 4, 'Tariffe e titoli di viaggio', 'Si classificano in questa posizione i documenti relativi alle attività di competenza delle Provincia in materia di politiche tariffarie dei trasporti pubblici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 1, 5, 'Servizi pubblici di navigazione', 'Si classificano in questa posizione i documenti relativi all’approvazione e alla concessione di servizi di navigazione di linea e non di linea, alla vigilanza sull’esercizio del trasporto pubblico, alla tutela della sicurezza, nonché agli eventuali contributi concessi in materia.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 1, 6, 'Trasporto aereo', 'Si classificano in questa posizione i documenti relativi alla gestione dei servizi pubblici di trasporto aereo. Si classificano altresì in questa posizione i documenti relativi a contributi erogati e a convenzioni e accordi stipulati in materia dalla Provincia con soggetti terzi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 1, 7, 'Vigilanza e controllo sui servizi di trasporto', 'Si classificano in questa posizione i documenti relativi alla tutela della sicurezza e all’attività di vigilanza sull’esercizio dei trasporti. Si classificano in questa posizione i documenti relativi alla connessa attività sanzionatoria.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 2, 0, 'Navigazione e gestione del demanio lacuale', 'Si classificano in questa posizione i documenti relativi all’attività di gestione e di vigilanza sulla sicurezza delle aree afferenti al demanio lacuale (ormeggi, spiagge, porti ecc.); si classificano in questa posizione, in particolare, i documenti relativi all’attività concessoria e autorizzativa all’uso di aree, opere, impianti ed altre pertinenze nelle zone portuali, nonché all’esercizio di attività particolari nelle medesime zone. Si classificano in questa posizione i documenti relativi all’attività autorizzativa e di vigilanza in materia di navigazione.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 3, 0, 'Navigazione aerea', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e di vigilanza in materia di navigazione aerea (sorvoli per motivi tecnico scientifici, addestramento ecc.). NOTA bene – I documenti relativi ad attività di programmazione o stipula di convenzioni in materia di trasporto aereo si classificano nella sottoclasse 20.1.6.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 0, 'Motorizzazione civile', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 1, 'Rilascio e rinnovo delle abilitazioni alla guida e patenti nautiche', 'Si classificano in questa posizione i documenti relativi al rilascio delle patenti di
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 2, 'Revisione, sospensione e revoca delle abilitazioni alla guida e patenti nautiche', 'Si classificano in questa posizione i documenti relativi a revisione, sospensione e revoca delle abilitazioni alla guida e delle patenti nautiche. In particolare si classificano in questa posizione i documenti relativi alla valutazione degli incidenti stradali e quelli relativi ai provvedimenti connessi alla gestione della patente a punti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 3, 'Scuole di guida e nautiche', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e di vigilanza in materia di scuole di guida e scuole nautiche. Si classificano, altresì, in questa posizione i documenti relativi alle abilitazioni all’esercizio della professione di insegnante e istruttore di scuola guida.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 4, 'Immatricolazioni dei veicoli e dei natanti', 'Si classificano in questa posizione i documenti relativi alle attività di immatricolazione dei veicoli e dei natanti nonché al rilascio, all’aggiornamento e alla gestione dei relativi documenti di circolazione e contrassegni.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 5, 'Revisioni e collaudi dei veicoli', 'Si classificano in questa posizione i documenti relativi alle attività di revisione e collaudo degli autoveicoli.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 6, 'Immatricolazioni, revisioni e collaudi delle unità di navigazione', 'Si classificano in questa posizione i documenti relativi alle attività di immatricolazione, visita e collaudo delle unità di navigazione, nonché al rilascio, all’aggiornamento e alla gestione dei relativi documenti di navigazione e contrassegni.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 7, 'Officine autorizzate', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e di vigilanza in materia di officine autorizzate all’effettuazione delle revisioni.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 8, 'Autotrasporto di persone e merci', 'Si classificano in questa posizione i documenti relativi alle autorizzazioni connesse all’esercizio dell’autotrasporto di persone e cose, ivi compresi i riconoscimenti delle relative idoneità professionali. Si classificano in questa posizione i documenti relativi alla tenuta dell’Albo provinciale degli autotrasportatori di cose in conto di terzi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 9, 'Studi di consulenza automobilistica', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e di vigilanza in materia di studi di consulenza automobilistica.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 10, 'Sicurezza e regolarità dell’esercizio ferroviario', 'Si classificano in questa posizione i documenti relativi agli adempimenti di competenza provinciale connessi all’attività autorizzativa per opere o interventi interferenti con la linea ferroviaria Trento-Malè, modifiche al tracciato e agli impianti di sicurezza nonché al materiale rotabile.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (20, 4, 11, 'Scale mobili e ascensori in servizio pubblico', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e di verifica tecnica degli impianti di scale mobili e ascensori in servizio pubblico.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 1, 0, 'Organizzazione preventiva della rete di protezione civile', 'Si classificano in questa posizione i documenti relativi alle attività di organizzazione preventiva dei sistemi di intervento di protezione civile. Si classificano in questa posizione i documenti relativi ai piani di protezione civile, alle esercitazioni, ai contributi destinati alle associazioni di protezione civile per l’acquisto di dotazioni, attrezzature ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 2, 0, 'Previsione e monitoraggio delle situazioni di rischio', 'Si classificano in questa posizione i documenti relativi all’attività di previsione e monitoraggio dei rischi, a prescindere dall’ambito in cui questi si manifestino. Si classificano in questa posizione i documenti relativi a sistemi di monitoraggio frane, sistemi di rilevamento idrometrici, pluviometrici, meteorologici ecc. Si classificano altresì in questa posizione i documenti relativi ad avvisi meteo e alla gestione del sistema allerta.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 3, 0, 'Rischio sismico', 'Si classificano in questa posizione i documenti relativi alla valutazione e monitoraggio del rischio sismico sul territorio provinciale (rete sismometrica, rete accelerometrica, microzonizzazione sismica ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 4, 0, 'Prevenzione in presenza di situazioni di rischio', 'Si classificano in questa posizione i documenti relativi agli interventi di prevenzione effettuati dalla Provincia o delegati ad enti terzi a seguito del riscontro di situazioni di rischio di ogni genere.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 5, 0, 'Gestione dell’emergenza e azioni di soccorso immediato', 'Si classificano in questa posizione i documenti relativi alla gestione delle situazioni di emergenza, alle azioni di soccorso, ivi comprese quelle condotte al di fuori del territorio provinciale. Si classificano in questa posizione i documenti relativi a servizi tecnici non urgenti svolti con l’impiego delle attrezzature in dotazione del corpo permanente dei vigili del fuoco (apertura delle porte di abitazioni in caso di guasti o chiusure accidentali, rifornimento di acqua potabile ad enti o privati in caso di siccità o guasti agli acquedotti, recupero di animali in difficoltà o animali pericolosi, messa in sicurezza di alberi o strutture pericolose ecc.). Si classificano in questa posizione i documenti relativi agli interventi connessi alle azioni di bonifica del terreno da ordigni bellici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 6, 0, 'Ripristino e ricostruzione a seguito di eventi calamitosi', 'Si classificano in questa posizione i documenti relativi agli interventi di ripristino e ricostruzione a seguito di eventi calamitosi a prescindere dalla loro natura. Si classificano in questa posizione anche i documenti relativi ai contributi erogati per attività di ripristino e risarcimento danni a seguito di eventi calamitosi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 7, 0, 'Prevenzione incendi', 'Si classificano in questa posizione i documenti relativi all’attività diretta alla prevenzione degli incendi sul territorio provinciale, in particolare all’istruttoria relativa all’esame dei progetti, alla formulazione di perizie e pareri, al rilascio dei nulla-osta provvisori dei certificati di prevenzione incendi e delle risposte ai quesiti formulati in materia. Si classificano, altresì, in questa posizione i documenti relativi alla valutazione e al controllo
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 8, 0, 'Corpo permanente dei vigili del fuoco e corpi volontari', 'Si classificano in questa posizione i documenti relativi all’attività di organizzazione e gestione del corpo permanente provinciale dei vigili del fuoco e dei corpi volontari.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 9, 0, 'Scuola provinciale antincendi', 'Si classificano in questa posizione i documenti relativi alle attività di formazione, qualificazione, aggiornamento ed addestramento condotte dalla scuola provinciale antincendi, nonché agli studi e alle ricerche di aggiornamento nei campi di applicazione delle tecniche di intervento e delle norme di prevenzione.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (21, 10, 0, 'Nucleo elicotteri', 'Si classificano in questa posizione i documenti relativi all’attività del nucleo elicotteri, in particolare i carteggi relativi a disciplinari e licenze, certificati di navigabilità, certificazioni, richieste intervento elicottero, rapporti di volo, addestramenti, rapporti con enti competenti in materia di aviazione, rapporti con ditte di fornitura e manutenzione, assicurazioni, aggiornamenti manuali ecc.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 1, 0, 'Fondo sanitario provinciale', 'Si classificano in questa posizione i documenti relativi all’individuazione e all’erogazione delle risorse finanziarie destinate al servizio sanitario provinciale, con specifico riferimento alla gestione del fondo sanitario provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 2, 0, 'Gestione delle strutture e sostegno all’edilizia sanitaria', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 2, 1, 'Attrezzature dell’Azienda provinciale per i servizi sanitari', 'Si classificano in questa posizione i documenti relativi agli interventi di finanziamento
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 2, 2, 'Edilizia dell’Azienda provinciale per i servizi sanitari', 'Si classificano in questa posizione i documenti relativi agli interventi di finanziamento delle attività edilizie dell’Azienda provinciale per i servizi sanitari.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 2, 3, 'Attrezzature delle residenze sanitarie assistenziali', 'Si classificano in questa posizione i documenti relativi agli interventi di finanziamento delle attività di acquisizione delle attrezzature delle residenze sanitarie assistenziali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 2, 4, 'Edilizia delle residenze sanitarie assistenziali', 'Si classificano in questa posizione i documenti relativi agli interventi di finanziamento delle attività edilizie delle residenze sanitarie assistenziali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 3, 0, 'Sistema tariffario, mobilità e rimborsi', 'Si classificano in questa posizione i documenti relativi alle competenze provinciali in materia di ticket e, in generale, relativi alla definizione della partecipazione alla spesa sanitaria da parte degli assistiti, nonché i documenti relativi alla determinazione delle tariffe per i servizi e le prestazioni sanitari e socio-sanitari, ivi comprese quelle attinenti all’ambito veterinario. Si classificano, inoltre, in questa posizione i documenti relativi alle attività connesse alla gestione della mobilità sanitaria e in particolare alle relative spese per la compensazione attiva e passiva. Si classificano in questa posizione anche i documenti relativi ai rimborsi relativi all’assistenza sanitaria indiretta.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 4, 0, 'Livelli di assistenza', 'Si classificano in questa posizione i documenti relativi alla definizione dei livelli di assistenza sanitaria, sia essenziali, ossia relativi alle prestazioni e ai servizi che il Servizio sanitario è tenuto a fornire a tutti i cittadini iscritti al Servizio sanitario nazionale, gratuitamente o dietro pagamento di una quota di partecipazione, sia aggiuntivi, ovvero erogati a favore degli iscritti al Servizio sanitario provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 5, 0, 'Programmazione sanitaria', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 5, 1, 'Piano sanitario provinciale', 'Si classificano in questa posizione i documenti relativi alle attività di elaborazione,
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 5, 2, 'Obiettivi specifici annuali', 'Si classificano in questa posizione i documenti relativi all’assegnazione all’Azienda provinciale per i servizi sanitari degli obiettivi specifici annuali di miglioramento, riqualificazione, razionalizzazione e sviluppo del Servizio sanitario provinciale, nonché i documenti relativi alla conseguente attività di monitoraggio e verifica sulla concreta realizzazione degli obiettivi assegnati.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 5, 3, 'Programmi di controllo e qualità', 'Si classificano in questa posizione i documenti relativi alla definizione e predisposizione degli atti di indirizzo ai fini dell’applicazione del sistema di controllo della qualità delle prestazioni, della qualità e dell’efficienza delle strutture e dei servizi sanitari nonché della fruizione delle prestazioni. Si classificano, altresì, in questa posizione i documenti relativi alla valutazione delle performance del Servizio sanitario provinciale o di sue parti, quali l’area della prevenzione, quella dell’assistenza distrettuale e quella dell’assistenza ospedaliera.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 5, 4, 'Pianificazione strutture e servizi sanitari', 'Si classificano in questa posizione i documenti relativi all’attività di programmazione sanitaria per la realizzazione, la ristrutturazione e la riorganizzazione di strutture e servizi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 6, 0, 'Gestione economico-finanziaria del sistema sanitario provinciale', 'Si classificano in questa posizione i documenti relativi all’esame, all’approvazione e al controllo degli atti di programmazione economico-finanziaria dell’Azienda provinciale per i servizi sanitari. Si classificano inoltre in questa posizione i documenti relativi all’attività di monitoraggio della gestione finanziaria e di verifica contabile dei consuntivi dell’Azienda provinciale per i servizi sanitari.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 7, 0, 'Organizzazione aziendale', 'Si classificano in questa posizione i documenti relativi all’attività di indirizzo, supervisione e controllo, per quanto di competenza provinciale, in merito all’organizzazione aziendale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 8, 0, 'Strutture sanitarie e socio-sanitarie', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 8, 1, 'Autorizzazione e accreditamento strutture ambulatoriali', 'Si classificano in questa posizione i documenti relativi ai procedimenti di autorizzazione
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 8, 2, 'Autorizzazione e accreditamento strutture ospedaliere', 'Si classificano in questa posizione i documenti relativi ai procedimenti di autorizzazione e accreditamento delle strutture ospedaliere. Si classificano in questa posizione i documenti relativi alle autorizzazioni in materia di pubblicità, nonché quelli relativi al funzionamento e all’organizzazione di tali strutture.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 8, 3, 'Autorizzazione e accreditamento strutture residenziali', 'Si classificano in questa posizione i documenti relativi ai procedimenti di autorizzazione e accreditamento delle strutture residenziali. Si classificano in questa posizione i documenti relativi alle autorizzazioni in materia di pubblicità, nonché quelli relativi al funzionamento e all’organizzazione di tali strutture.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 8, 4, 'Autorizzazione e accreditamento enti ausiliari', 'Si classificano in questa posizione i documenti relativi ai procedimenti di autorizzazione e accreditamento degli enti ausiliari. Si classificano in questa posizione i documenti relativi alle autorizzazioni in materia di pubblicità, nonché quelli relativi al funzionamento e all’organizzazione di tali strutture.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 8, 5, 'Autorizzazione e accreditamento stabilimenti termali e di imbottigliamento di acque minerali', 'Si classificano in questa posizione i documenti relativi ai procedimenti di autorizzazione e accreditamento degli stabilimenti termali e di imbottigliamento delle acque minerali. Si classificano in questa posizione i documenti relativi alle autorizzazioni in materia di pubblicità, nonché quelli relativi al funzionamento e all’organizzazione di tali strutture.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 8, 6, 'Funzionamento e organizzazione strutture pubbliche', 'Si classificano in questa posizione i documenti relativi all’attività di controllo svolta dalla Provincia in merito alla gestione degli edifici pubblici con destinazione sanitaria (pareri sul cambiamento di sede di un ambulatorio ecc.) nonché quelli relativi alla verifica della
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 8, 7, 'Funzionamento e organizzazione strutture private', 'Si classificano in questa posizione i documenti relativi all’attività di controllo svolta dalla Provincia in merito alla gestione degli edifici privati con destinazione sanitaria, nonché quelli relativi alla verifica della sussistenza dei requisiti per beneficiare dei finanziamenti a carico del fondo sanitario provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 9, 0, 'Professioni sanitarie', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 9, 1, 'Titoli ed esercizio professionale', 'Si classificano in questa posizione i documenti relativi ai requisiti per l’esercizio delle
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 9, 2, 'Responsabilità professionale', 'Si classificano in questa posizione i documenti relativi alle verifiche e ai controlli, per quanto attribuibili alle competenze provinciali, relativi ai profili di responsabilità delle professioni sanitarie. Si classificano in questa posizione i documenti relativi alle segnalazioni di sanzioni emanate da ordini e collegi professionali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 10, 0, 'Formazione sanitaria', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 10, 1, 'Formazione sanitaria professionale', 'Si classificano in questa posizione i documenti relativi alla promozione, organizzazione
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 10, 2, 'Formazione sanitaria universitaria', 'Si classificano in questa posizione i documenti relativi alla promozione, organizzazione e gestione di attività formative di base e specialistiche destinate a personale sanitario e rivolte al conseguimento di titoli di studio universitari.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 10, 3, 'Formazione sanitaria continua', 'Si classificano in questa posizione i documenti relativi a promozione, organizzazione e gestione di attività di aggiornamento e/o formazione permanente destinate a personale operante in ambito sanitario.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 11, 0, 'Personale sanitario', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 11, 1, 'Personale convenzionato', 'Si classificano in questa posizione i documenti relativi alla definizione e applicazione
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 11, 2, 'Personale dipendente', 'La sottoclasse comprende i documenti relativi alla definizione e applicazione dei contratti collettivi provinciali di lavoro, nonché degli indirizzi relativi alle attività, compresa quella libero-professionale, o comunque alle problematiche del personale dipendente dal Servizio sanitario provinciale. Si classificano, altresì, in questa posizione i documenti relativi alle problematiche, per quanto di competenza della Provincia, relative al personale dipendente da strutture sanitarie private.');
