INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 14, 2, 'Strutture farmaceutiche', 'Si classificano in questa posizione i documenti relativi alla pianificazione, alla titolarità e alla gestione delle strutture farmaceutiche.');
