INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 15, 2, 'Medicina dello sport', 'Si classificano in questa posizione i documenti relativi alle attività, per quanto di competenza provinciale, nell’ambito della medicina dello sport.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 15, 3, 'Sorveglianza e profilassi delle malattie infettive e diffusive', 'Si classificano in questa posizione i documenti relativi alle attività, per quanto di competenza provinciale, nell’ambito della sorveglianza e profilassi delle malattie infettive e diffusive.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 15, 4, 'Igiene, sicurezza e medicina del lavoro', 'Si classificano in questa posizione i documenti relativi alle attività, per quanto di competenza provinciale, nell’ambito della prevenzione nei luoghi di lavoro e delle malattie professionali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 15, 5, 'Igiene urbana e ambientale', 'Si classificano in questa posizione i documenti relativi alle attività, per quanto di competenza provinciale, nell’ambito della vigilanza e controllo sull’inquinamento ambientale e circa la sicurezza dei prodotti, compresi i cosmetici.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 15, 6, 'Promozione della salute e prevenzione sanitaria', 'Si classificano in questa posizione i documenti relativi alle attività, per quanto di competenza provinciale, nell’ambito della promozione della salute e della prevenzione sanitaria.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 16, 0, 'Igiene e sanità pubblica veterinaria', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza e profilassi delle malattie animali, ivi compresa la gestione delle allerte. Si classificano in questa posizione anche i documenti relativi agli indennizzi per l’abbattimento bestiame.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 17, 0, 'Sicurezza alimentare', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 17, 1, 'Igiene degli alimenti', 'Si classificano in questa posizione i documenti relativi a tutti gli aspetti che ineriscono
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 17, 2, 'Fitosanitari e residui', 'Si classificano in questa posizione i documenti relativi alla corretta applicazione delle norme comunitarie in materia di produzione, commercio, utilizzo di fitosanitari e presenza di residui degli stessi sui prodotti vegetali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (22, 17, 3, 'Anagrafe, alimentazione e benessere animale', 'Si classificano in questa posizione i documenti relativi alla sorveglianza sanitaria esercitata sugli allevamenti, sulle strutture di cura e sugli aspetti sanitari connessi alle pratiche di riproduzione; si classificano, altresì, in questa posizione i documenti relativi alle attività di controllo sulla movimentazione e sul trasporto, ivi compreso il benessere animale. Si classificano in questa posizione anche i documenti relativi all’attività di vigilanza in materia di produzione e distribuzione degli alimenti per animali, ivi compresi i controlli sui mangimi e sul loro import/export.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 1, 0, 'Pianificazione sociale', 'Si classificano in questa posizione i documenti relativi all’attività di pianificazione territoriale e provinciale in materia di politiche sociali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 2, 0, 'Gestione economico finanziaria dei servizi socio-assistenziali', 'Si classificano in questa posizione i documenti relativi alla gestione economico-finanziaria del comparto dei servizi socio-assistenziali, nonché quelli relativi all’attività di monitoraggio della gestione finanziaria di tali servizi, con specifico riferimento alle verifiche contabili sui soggetti gestori.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 3, 0, 'Coordinamento, sviluppo e vigilanza sul sistema dei servizi socio-assistenziali', 'Si classificano in questa posizione i documenti relativi all’attività di coordinamento, monitoraggio e valutazione del sistema dei servizi socio-assistenziali e dei soggetti gestori. Si classificano in particolare in questa posizione i documenti relativi all’attività di riconoscimento e iscrizione al registro dei soggetti idonei al convezionamento, nonché alla conseguente attività di verifica della permanenza dei requisiti richiesti e a quella di consulenza fornita ai soggetti gestori di servizi socio-assistenziali. Si classificano inoltre in questa posizione i documenti relativi alle autorizzazioni al funzionamento di strutture residenziali e semiresidenziali, nonché i documenti relativi all’attività di predisposizione e coordinamento degli interventi finalizzati allo sviluppo e all’innovazione nei servizi socio- assistenziali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 4, 0, 'Gestione delle strutture e sostegno all’edilizia socio-assistenziale', 'Si classificano in questa posizione i documenti relativi alla gestione e finanziamento degli interventi, diretti o sostenuti, per l’acquisizione di attrezzature ed arredi e in materia di edilizia socio-assistenziale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 5, 0, 'Promozione e sostegno del volontariato sociale', 'Si classificano in questa posizione i documenti relativi all’attività di promozione e sostegno del volontariato sociale. Si classificano in questa posizione i documenti relativi alla tenuta dell’Albo del volontariato.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 6, 0, 'Interventi socio-assistenziali e servizi sociali 23.6.1 Minori', 'Si classificano in questa posizione i documenti relativi agli interventi socio- assistenziali e ai servizi sociali erogati a favore di minori. Si classificano in particolare in questa posizione i documenti relativi agli interventi connessi alle attività riferite ai percorsi di accoglienza e alle pratiche di affidamento e adozione.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 6, 2, 'Adulti', 'Si classificano in questa posizione i documenti relativi agli interventi socio- assistenziali e ai servizi sociali erogati a favore degli adulti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 6, 3, 'Anziani', 'Si classificano in questa posizione i documenti relativi agli interventi socio- assistenziali e ai servizi sociali erogati a favore dei soggetti anziani.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 6, 4, 'Disabili', 'Si classificano in questa posizione i documenti relativi agli interventi socio- assistenziali e ai servizi sociali erogati a favore dei soggetti disabili. Si classificano in particolare in questa posizione i documenti relativi ai servizi di trasporto e agli interventi in materia di eliminazione o superamento delle barriere architettoniche.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 6, 5, 'Immigrati', 'Si classificano in questa posizione i documenti relativi alle attività volte a favorire i processi di integrazione degli immigrati sul territorio provinciale. Si classificano in questa posizione i documenti relativi ai contributi erogati per iniziative di mediazione culturale, per soggetti operanti nel settore dell’immigrazione, per attività di assistenza e orientamento. Si classificano inoltre in questa posizione i documenti relativi alla gestione degli alloggi destinati agli immigrati.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 7, 0, 'Politiche abitative e interventi per l’edilizia abitativa', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 7, 1, 'Edilizia abitativa agevolata', 'Si classificano in questa posizione i documenti relativi alla pianificazione e ai
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 7, 2, 'Edilizia abitativa pubblica', 'Si classificano in questa posizione i documenti relativi alla programmazione e al coordinamento delle attività in materia di edilizia abitativa pubblica e ai contributi per la realizzazione di alloggi pubblici e il pagamento del contributo integrativo. Si classificano inoltre in questa posizione i documenti relativi all’attività consulenziale svolta nei confronti dei soggetti coinvolti nell’attuazione della politica provinciale per la casa.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 8, 0, 'Formazione e aggiornamento degli operatori dei servizi socio-assistenziali', 'Si classificano in questa posizione i documenti relativi alle attività di formazione e aggiornamento degli operatori dei servizi socio-assistenziali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 9, 0, 'Provvidenze economiche assistenziali', 'Si classificano in questa posizione i documenti relativi agli interventi in materia di assistenza e in particolare alle provvidenze a carattere continuativo in favore di soggetti ciechi civili, sordomuti, mutilati, invalidi civili, danneggiati da trasfusioni o vaccinazioni obbligatorie o colpiti da eventuali altri danni alla salute.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 10, 0, 'Previdenza integrativa', 'Si classificano in questa posizione i documenti relativi agli interventi in materia di previdenza integrativa e pacchetto famiglia, volti a sostenere la cura e l’educazione dei figli, la copertura previdenziale di alcune categorie di lavoratori e le tutela previdenziale del lavoro casalingo, nonché le rendite previste a fronte di specifiche condizioni connesse con lo svolgimento dell’attività lavorativa.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 11, 0, 'Politiche per la natalità, il benessere e il reddito familiare', 'Si classificano in questa posizione i documenti relativi alla definizione, al coordinamento e alla realizzazione delle politiche familiari e di sostegno alla natalità. Si classificano in particolare in questa posizione i documenti relativi all’organizzazione di progetti e gestione dei servizi volti a favorire il benessere familiare (“Distretto famiglia”, associazionismo familiare, punti informativi per le famiglie, strategie sui tempi del territorio ecc). Si
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 12, 0, 'Politiche giovanili', 'Si classificano in questa posizione i documenti relativi alla programmazione, al finanziamento, alla gestione e al monitoraggio degli interventi a favore della popolazione giovanile.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 13, 0, 'Servizio civile', 'Si classificano in questa posizione i documenti relativi alle attività di sviluppo e gestione del sistema di servizio civile.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 14, 0, 'Solidarietà internazionale ed educazione alla pace', 'Si classificano in questa posizione i documenti relativi alla solidarietà internazionale, ivi compresi i progetti di sviluppo e di cooperazione. Si classificano in questa posizione i documenti relativi all’educazione alla cultura della pace.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 15, 0, 'Emigrazione trentina all’estero', 'Si classificano in questa posizione i documenti relativi alla programmazione, al coordinamento e alla realizzazione d’interventi a favore dei trentini emigrati all’estero. Si classificano in particolare in questa posizione i documenti relativi a rapporti con associazioni di volontariato attive nell’ambito dell’emigrazione trentina, con soggetti giuridici all’estero e con emigrati e loro discendenti, e i documenti relativi agli interventi goduti in territorio trentino o usufruiti all’estero (contributi, borse di studio, scambi tra giovani ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (23, 16, 0, 'Promozione e sostegno delle attività sportive', 'Si classificano in questa posizione i documenti relativi all’attività di promozione e sostegno delle attività sportive, ivi compresa la sovvenzione di associazioni, società e altri soggetti operanti in tale ambito. Si classificano in questa posizione i documenti relativi ai contributi destinati alla realizzazione, ampliamento, ristrutturazione e gestione degli impianti sportivi. Si classificano inoltre in questa posizione i documenti relativi all’organizzazione di manifestazioni sportive curate o patrocinate dalle strutture provinciali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (24, 1, 0, 'Vigilanza sul lavoro', 'Si classificano in questa posizione i documenti relativi all’attività ispettiva e di verifica del rispetto delle norme in materia di lavoro da parte degli uffici preposti alla vigilanza nei confronti delle aziende, nonché i documenti relativi all’attività di vigilanza sull’esecuzione dei contratti collettivi di lavoro.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (24, 2, 0, 'Tutela del lavoro', 'Si classificano in questa posizione i documenti relativi all’attività autorizzativa e consultiva in materia di tutela del lavoro, in relazione anche a specifici ambiti (minori, madri, extracomunitari, orari e riposi, turni di lavoro, flessibilità delle forme contrattuali di lavoro ecc.). Si classificano altresì in questa posizione i documenti relativi all’erogazione di benefici contributivi e al deposito obbligatorio di atti quali i regolamenti delle cooperative.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (24, 3, 0, 'Sistema provinciale per l’impiego', 'Si classificano in questa posizione i documenti relativi alla gestione del sistema provinciale per l’impiego, ivi compresa la tenuta delle relative banche dati, liste e elenchi (elenco anagrafico dei lavoratori, curricula ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (24, 4, 0, 'Inserimento e reinserimento lavorativo', 'Si classificano in questa posizione i documenti relativi agli interventi volti a garantire l’inserimento o il reinserimento nel mercato del lavoro, anche di soggetti deboli e svantaggiati. Si classificano, altresì, in questa posizione i documenti relativi agli interventi di sostegno per l’avvio e il consolidamento di piccole imprese. Si classificano inoltre in questa posizione i documenti relativi all’incentivazione del reimpiego di personale con qualifica dirigenziale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (24, 5, 0, 'Orientamento professionale e incontro tra domanda e offerta di lavoro', 'Si classificano in questa posizione i documenti relativi alle attività, dirette o sostenute, volte a favorire l’orientamento professionale e l’incontro tra la domanda e l’offerta di lavoro.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (24, 6, 0, 'Iniziative formative e aggiornamento professionale', 'Si classificano in questa posizione i documenti relativi all’attività di supporto, coordinamento e monitoraggio delle iniziative formative per l’inserimento e l’aggiornamento dei lavoratori.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (24, 7, 0, 'Ammortizzatori sociali', 'Si classificano in questa posizione i documenti relativi al sostegno al reddito dei lavoratori in caso di perdita o sospensione del lavoro.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (24, 8, 0, 'Attività arbitrale e conciliativa', 'Si classificano in questa posizione i documenti relativi all’attività arbitrale e ai tentativi di conciliazione in materia di lavoro in sede di camera arbitrale e di conciliazione monocratica.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 1, 0, 'Agevolazioni e contributi per attività culturali, tutela e valorizzazione dei beni culturali', 'Si classificano in questa posizione i documenti relativi ai contributi erogati a soggetti pubblici e privati, ivi compresi musei, scuole musicali, associazioni culturali ecc. per il funzionamento ordinario, l’acquisto di attrezzature, strutture e arredi, interventi conservativi, nonché per attività di valorizzazione e per organizzazione di manifestazioni ed eventi (convegni, congressi, mostre).
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 2, 0, 'Coordinamento e vigilanza su istituzioni e attività culturali', 'Si classificano in questa posizione i documenti relativi alle attività di riconoscimento, coordinamento e vigilanza sulle istituzioni (musei, associazioni, scuole musicali, eco-musei ecc.) e le attività culturali, da chiunque svolte. Si classificano in questa posizione i documenti relativi all’attività di coordinamento con associazioni che si occupano di vestigia della Grande guerra.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 3, 0, 'Coordinamento, assistenza e sostegno al sistema bibliotecario trentino', 'Si classificano in questa posizione i documenti relativi alle attività di indirizzo, coordinamento, assistenza tecnica e giuridica e sostegno nei confronti dei soggetti coinvolti nel Sistema bibliotecario trentino, ivi comprese le attività inerenti alla formulazione di criteri per l’unificazione dei metodi e delle tecniche biblioteconomiche, alla promozione della cooperazione interbibliotecaria ecc.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 4, 0, 'Valorizzazione dei beni culturali', 'Si classificano in questa posizione i documenti relativi alle attività di studio e ricerca di competenza degli uffici di tutela, nonché alle attività didattiche e divulgative dirette a promuovere la conoscenza del patrimonio culturale e ad assicurare le migliori condizioni di fruizione del patrimonio stesso. Si classificano in particolare in questa posizione i documenti relativi agli interventi di musealizzazione, di gestione di aree archeologiche e di musei. Si classificano in questa posizione anche i documenti relativi all’assegnazione di premi per tesi di laurea.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 5, 0, 'Tutela dei beni archeologici', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 5, 1, 'Individuazione, scavo e ricerca', 'Si classificano in questa posizione i documenti relativi all’attività preventiva di
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 5, 2, 'Vigilanza', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza svolta nei confronti dei beni archeologici, ivi compresi i procedimenti autorizzativi, nonché i documenti relativi all’autorizzazione e al controllo sulla movimentazione e la circolazione di beni archeologici. Si classificano in questa posizione anche i documenti relativi ai ricorsi, alle denunce e all''attività sanzionatoria.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 5, 3, 'Interventi conservativi e di restauro', 'Si classificano in questa posizione i documenti relativi alle attività di recupero, trasporto, analisi e restauro dei beni archeologici gestite in diretta amministrazione, ivi compresi quelli eseguiti su beni ricadenti in aree archeologiche.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 5, 4, 'Catalogazione', 'Si classificano in questa posizione i documenti relativi ai lavori di ricognizione inventariale, sistemazione, organizzazione, catalogazione di reperti e beni archeologici mobili e immobili, elaborati grafici, materiale fotografico.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 6, 0, 'Tutela dei beni architettonici', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 6, 1, 'Individuazione e vigilanza', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza svolta
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 6, 2, 'Catalogazione e georeferenziazione', 'Si classificano in questa posizione i documenti relativi ai lavori di catalogazione e georeferenziazione di beni architettonici soggetti a tutela. Si classificano in questa posizione i documenti relativi alla descrizione grafica e fotografica di tali beni.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 6, 3, 'Interventi conservativi e di restauro', 'Si classificano in questa posizione i documenti relativi alle attività di analisi e restauro dei beni architettonici gestite in diretta amministrazione da parte della Provincia.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 7, 0, 'Tutela dei beni archivistici', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 7, 1, 'Vigilanza sugli archivi privati e degli enti pubblici', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza svolta
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 7, 2, 'Lavori storico-archivistici', 'Si classificano in questa posizione i documenti relativi ai lavori di censimento, ordinamento e inventariazione effettuati in diretta amministrazione dalla Provincia su beni archivistici vigilati.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 7, 3, 'Interventi conservativi, restauro e riproduzione', 'Si classificano in questa posizione i documenti relativi alle attività volte ad assicurare la corretta conservazione del materiale archivistico di soggetti vigilati quali interventi di restauro, spolveratura, disinfestazione, legatura ecc. gestiti in diretta amministrazione da parte della Provincia. Si classificano inoltre in questa posizione i documenti relativi alle operazioni di riproduzione.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 8, 0, 'Tutela dei beni librari', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 8, 1, 'Individuazione e vigilanza', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza svolta
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 8, 2, 'Catalogazione e inventariazione', 'Si classificano in questa posizione i documenti relativi ai lavori di catalogazione e inventariazione effettuati in diretta amministrazione dalla Provincia su beni librari soggetti a tutela.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 8, 3, 'Interventi conservativi, restauro e riproduzione', 'Si classificano in questa posizione i documenti relativi alle attività volte ad assicurare la corretta conservazione del materiale librario soggetto a tutela quali interventi di restauro, spolveratura, disinfestazione, legatura ecc. gestiti in diretta amministrazione da parte della Provincia. Si classificano inoltre in questa posizione i documenti relativi alle operazioni di riproduzione.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 9, 0, 'Tutela dei beni storico artistici e etnoantropologici', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 9, 1, 'Individuazione e vigilanza', 'Si classificano in questa posizione i documenti relativi all’attività di vigilanza svolta
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 9, 2, 'Catalogazione', 'Si classificano in questa posizione i documenti relativi ai lavori di catalogazione effettuati su beni storico artistici e etnoantropologici soggetti a tutela. Si classificano altresì in questa posizione i documenti relativi alle attività di formulazione di criteri per l’unificazione dei metodi e delle tecniche catalografiche, nonché al coordinamento delle operazioni di catalogazione svolte da terzi.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 9, 3, 'Interventi conservativi e di restauro', 'Si classificano in questa posizione i documenti relativi alle attività di recupero, trasporto, analisi e restauro dei beni storico-artistici gestite direttamente dalla Provincia, ivi compresa la relativa documentazione grafica e fotografica.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 10, 0, 'Toponomastica', 'Si classificano in questa posizione i documenti relativi all’uso della toponomastica locale. Si classificano in questa posizione anche i documenti relativi all’attività di studio in materia di toponomastica storica, nonché a quella di compilazione e aggiornamento del Dizionario toponomastico trentino.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (25, 11, 0, 'Operatori del settore dei beni e delle attività culturali', 'Si classificano in questa posizione i documenti relativi all’organizzazione, al sostegno e alla gestione delle attività formative e di aggiornamento degli operatori dei beni e delle attività culturali da parte della Provincia. Si classificano altresì in questa posizione i documenti relativi alle attestazioni e certificazioni, per quanto di competenza provinciale, in materia di profili professionali degli operatori del settore.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 1, 0, 'Pianificazione del sistema educativo provinciale', 'Si classificano in questa posizione i documenti relativi all’attività di pianificazione connessa al sistema educativo provinciale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 2, 0, 'Coordinamento e vigilanza sul sistema dei servizi per la prima infanzia', 'Si classificano in questa posizione i documenti relativi all’attività di pianificazione, monitoraggio, valutazione e coordinamento del sistema dei servizi per la prima infanzia e in particolare dei soggetti gestori. Si classificano in questa posizione anche i documenti relativi all’attività di riconoscimento dei soggetti gestori, alla conseguente attività di controllo sulla sussistenza dei requisiti strutturali, organizzativi ecc., nonché alla valutazione delle relative attività.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 3, 0, 'Gestione economico-finanziaria del sistema educativo provinciale', 'Si classificano in questa posizione i documenti relativi alla gestione economico-finanziaria del sistema educativo provinciale (scuole dell''infanzia, istituti scolastici e formativi), nonché quelli relativi all’attività di monitoraggio della gestione finanziaria, con specifico riferimento alle verifiche contabili dei programmi di spesa degli enti gestori o delle istituzioni scolastiche e formative provinciali e paritarie. Si classificano in questa posizione anche i documenti relativi a consulenza, vigilanza e controllo sull’attività contabile delle istituzioni scolastiche e formative e, in particolare, sulla rendicontazione delle istituzioni formative paritarie.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 4, 0, 'Coordinamento e vigilanza sul sistema educativo provinciale 26.4.1 Scuole dell’infanzia', 'Si classificano in questa posizione i documenti relativi al coordinamento, al monitoraggio, alla valutazione e alla vigilanza sul funzionamento e sulle attività delle scuole dell’infanzia, nonché dei relativi soggetti gestori. Si classificano in questa posizione anche i documenti relativi all’attività di supporto fornita alle scuole ed ai circoli di coordinamento.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 4, 2, 'Istituzioni scolastiche', 'Si classificano in questa posizione i documenti relativi al coordinamento, al monitoraggio, alla valutazione e alla vigilanza sul funzionamento e sulle attività delle istituzioni scolastiche. Si classificano in questa posizione anche i documenti relativi all’attività di supporto fornita alle istituzioni scolastiche.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 4, 3, 'Istituzioni formative', 'Si classificano in questa posizione i documenti relativi al coordinamento, al monitoraggio, alla valutazione e alla vigilanza sul funzionamento e sulle attività delle istituzioni formative provinciali e paritarie, nonché su quella dei relativi soggetti gestori. Si classificano in questa posizione anche i documenti relativi all’attività di supporto fornita alle istituzioni formative.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 5, 0, 'Politica del personale del sistema educativo provinciale e organici', 'Si classificano in questa posizione i documenti relativi alla gestione delle relazioni sindacali e alla contrattazione del personale operante nel settore della scuola. Si classificano in questa
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 6, 0, 'Sviluppo e innovazione dei servizi per la prima infanzia e del sistema educativo provinciale', 'Si classificano in questa posizione i documenti relativi alle attività di promozione, coordinamento, sostegno e monitoraggio degli interventi volti allo sviluppo e all’innovazione dei servizi per la prima infanzia e del sistema educativo provinciale, quali i progetti di diffusione delle lingue straniere, di educazione alla salute e di coordinamento delle attività motorie e sportive. Si classificano inoltre in questa posizione i documenti relativi a progetti di ricerca, innovazione e sperimentazione, anche di livello internazionale, applicati al campo educativo (introduzione delle tecnologie didattiche ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 7, 0, 'Interventi di integrazione scolastica', 'Si classificano in questa posizione i documenti relativi agli interventi pianificati, coordinati o eseguiti direttamente dalla Provincia in materia di servizi di sostegno e integrazione scolastica (servizi agli studenti con bisogni educativi speciali, progetti di integrazione degli alunni stranieri, soggiorni educativi, colonie, campeggi ecc.). Si classificano in questa posizione anche i documenti relativi all’attività di accreditamento dei soggetti che operano nel campo degli interventi per gli studenti con bisogni educativi speciali.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 8, 0, 'Diritto allo studio', 'Si classificano in questa posizione i documenti relativi alla materia del diritto allo studio ordinario e universitario per quanto di competenza provinciale (servizi mensa, trasporto alunni, libri di testo, assegni e borse di studio, prolungamento di orario per la scuola dell’infanzia, assicurazioni scolastiche, agevolazioni tariffarie ecc.).');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 9, 0, 'Esami di stato, di qualifica professionale e abilitazioni alle libere professioni', 'Si classificano in questa posizione i documenti relativi alla gestione degli esami di stato (I e II ciclo della scuola secondaria), di qualifica professionale e di abilitazione all’esercizio delle libere professioni. Si classificano in questa posizione i documenti relativi al riconoscimento dell’equipollenza dei titoli di studio, nonché quelli relativi alla valutazione degli studenti e alla conseguente emissione dei titoli di studio.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 10, 0, 'Gestione delle strutture e sostegno all’edilizia scolastica', 'Si classificano in questa posizione i documenti relativi agli interventi, diretti o sostenuti, in materia di acquisizione di attrezzature e arredi per scuole dell''infanzia equiparate e istituzioni scolastiche e formative provinciali e paritarie. Si classificano inoltre in questa posizione i documenti relativi ai contributi erogati a favore di scuole dell’infanzia equiparate e istituzioni scolastiche e formative paritarie per interventi di edilizia scolastica.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 11, 0, 'Formazione continua e aggiornamento degli operatori dei servizi socio-educativi per la prima infanzia e della scuola', 'Si classificano in questa posizione i documenti relativi alle attività di formazione continua e aggiornamento degli operatori dei servizi socio-educativi per la prima infanzia, degli operatori delle scuole dell’infanzia e delle istituzioni scolastiche e formative (personale educativo dei servizi socio-educativi per la prima infanzia e della scuola per l’infanzia, personale insegnante, coordinatori pedagogici, assistenti educatori, ATA ecc.). Si classificano in questa posizione anche i documenti relativi alle certificazioni linguistiche rilasciate al personale docente.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 12, 0, 'Alta formazione professionale', 'Si classificano in questa posizione i documenti relativi alla programmazione, progettazione, affidamento, finanziamento, coordinamento, valutazione e monitoraggio dei percorsi di alta formazione professionale.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 13, 0, 'Istruzione universitaria e alta formazione musicale, artistica e coreutica', 'Si classificano in questa posizione i documenti relativi al coordinamento dei rapporti intrattenuti tra Provincia e Università degli studi di Trento, in particolare in relazione alle attività connesse alla pianificazione, promozione e finanziamento di interventi in materia di istruzione universitaria. Si classificano inoltre in questa posizione i documenti relativi alle competenze provinciali in materia di organismi di alta formazione musicale, artistica e coreutica.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 14, 0, 'Innovazione, ricerca scientifica e tecnologica', '');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 14, 1, 'Promozione della ricerca scientifica', 'Si classificano in questa posizione i documenti relativi alla promozione e al
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 14, 2, 'Valorizzazione degli esiti della ricerca scientifica', 'Si classificano in questa posizione i documenti relativi alle attività di valorizzazione dei risultati conseguiti dai progetti di ricerca scientifica e tecnologica e alla tutela legale degli stessi. Si classificano in particolare in questa posizione i documenti relativi alla concessione di contributi per l’attivazione sul territorio di distretti tecnologici o di poli di innovazione, al sostegno dell’avvio di nuove attività imprenditoriali e al trasferimento tecnologico.
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (26, 15, 0, 'Educazione permanente e degli adulti', 'La classe comprende la documentazione relativa all’attività di programmazione, progettazione, coordinamento, promozione, sostegno e monitoraggio degli interventi in materia di educazione permanente e degli adulti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (27, 1, 0, 'Tenuta e conservazione', 'Si classificano in questa posizione i documenti relativi alle attività connesse alla tenuta e alla conservazione del Catasto fondiario (ivi comprese le variazione di coltura), geometrico e fabbricati. Si classificano in questa posizione anche i documenti relativi alle trasmissioni di volture a e da enti extra-provinciali competenti in materia catastale o da privati.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (27, 2, 0, 'Controllo, verifica e ispezione', 'Si classificano in questa posizione i documenti relativi alle attività di controllo, verifica e ispezione in materia di tenuta del Catasto fondiario, geometrico e fabbricati, ivi comprese le relative sanzioni. Si classificano altresì in questa posizione i documenti relativi alle richieste di verifiche dei dati catastali provenienti da soggetti anche esterni all’Amministrazione.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (27, 3, 0, 'Rilevazione e revisione degli estimi catastali', 'Si classificano in questa posizione i documenti relativi alla formazione di nuove cartografie e alla revisione periodica degli estimi catastali e dei classamenti.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (27, 4, 0, 'Certificazione e rilascio dei dati catastali', 'Si classificano in questa posizione i documenti relativi all’attività certificativa svolta sulla scorta degli atti catastali e all’attività relativa all’estrazione e alla fornitura di dati.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (28, 1, 0, 'Impianto, reimpianto, ripristino e completamento', 'Si classificano in questa posizione i documenti relativi alle attività di impianto, reimpianto, ripristino e completamento del Libro fondiario. Si classificano in questa posizione i documenti relativi all’attività delle commissioni di ripristino.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (28, 2, 0, 'Controllo, verifica e ispezione', 'Si classificano in questa posizione i documenti relativi alle attività di controllo, verifica e ispezione in merito alla tenuta del Libro fondiario.');
INSERT INTO PROTOCOLLO.FASCICOLO (CATEGORIA, CLASSE, FASCICOLO, DESCRIZIONE, NOTE) VALUES (28, 3, 0, 'Pubblicazione e trattazione tavolare delle mappe', 'Si classificano in questa posizione i documenti relativi alla pubblicazione e alla trattazione tavolare conseguenti alla produzione di nuove mappe.');
